const html = $input.first().json.data;
const headers = $input.first().json.headers || {};
const cookies = headers['set-cookie'] || [];

if (!html) {
  throw new Error("Unable to load TikTok page. Please try again!");
}

// Find JSON data from script tag
const regex = /<script id="__UNIVERSAL_DATA_FOR_REHYDRATION__" type="application\/json">([\s\S]*?)<\/script>/;
const match = html.match(regex);

if (!match) {
  throw new Error("Video data not found. The link may be deleted or private.");
}

const jsonStr = match[1];
let data;

try {
  data = JSON.parse(jsonStr);
} catch (e) {
  throw new Error("Unable to parse video data. Please try again!");
}

// Get video URL from multiple possible paths
const itemInfo = data?.__DEFAULT_SCOPE__?.["webapp.video-detail"]?.itemInfo?.itemStruct;
const videoUrl = itemInfo?.video?.playAddr || itemInfo?.video?.downloadAddr;

if (!videoUrl) {
  throw new Error("Video URL not found. The video may be restricted from downloading.");
}

const videoInfo = {
  videoUrl: videoUrl,
  cookies: cookies.join('; '),
  description: itemInfo?.desc || 'TikTok Video',
  author: itemInfo?.author?.uniqueId || 'unknown',
  stats: {
    plays: itemInfo?.stats?.playCount || 0,
    likes: itemInfo?.stats?.diggCount || 0,
    comments: itemInfo?.stats?.commentCount || 0
  }
};

return [{ json: videoInfo }];

