parameters.inputSchema:
={
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "lyrical_style": {
      "type": "string",
      "description": "The genre or style of the song (e.g., Pop, R&B, Rap, Rock, Country, Hip-Hop, Indie, Folk)"
    },
    "lyrics": {
      "type": "string",
      "description": "Complete song lyrics with all sections labeled (e.g., [VERSE 1], [CHORUS], [BRIDGE])"
    }
  },
  "required": ["lyrical_style", "lyrics"],
  "additionalProperties": false
}
