const codes = [
  ...new Set(
    (lines || [])
      .map((l) => String(l.name || "").trim())
      .filter(Boolean)
  ),
];

if (codes.length === 0) {
  throw new Error("No product codes found in lines[].name");
}

// Escape single quotes for SOQL
const soqlList = codes
  .map((c) => `'${c.replace(/'/g, "\\'")}'`)
  .join(",");

const soql = `
  SELECT Id, Product2Id, Product2.ProductCode
  FROM PricebookEntry
  WHERE Pricebook2Id = '${pricebook2Id}'
    AND Product2.ProductCode IN (${soqlList})
`.trim();

return [{ soql, codes }];

