      jd_filename: jd.jd_filename,
      jd_file_id: jd.jd_file_id
    }));
  } else {
    // For telegram_msg_match, return single item - FIX: use jd_match not telegram_msg_match
    return [{
      jd_filename: $json.output.jd_match.jd_filename,
      jd_file_id: $json.output.jd_match.jd_file_id
    }];
  }

// Get the selected filename from the AI agent output
const selectedFilename = $json.output.selected_jd.jd_filename;

// Get all the JD data from the loop output
const allJDs = $('Loop Over Items').all();

// Find the matching JD by filename
const selectedJD = allJDs.find(jd => jd.json.jd_filename === selectedFilename);

