// Function node: extract canonical /in/<slug> profile URL
const trig = $('Receive Telegram Msg to Recruiter Bot').first().json || {};
const msg  = trig.message || trig.update?.message || trig.channel_post || {};
const txt  = ((msg.text ?? msg.caption ?? '') + '');

// 1) Collect candidates from Telegram entities (most reliable)
let candidates = [];
const entities = msg.entities ?? msg.caption_entities ?? [];
for (const e of entities) {
  if (e.type === 'url') {
    const start = e.offset ?? 0;
    const end   = start + (e.length ?? 0);
    candidates.push(txt.slice(start, end));
  } else if (e.type === 'text_link' && e.url) {
    candidates.push(e.url);
  }
}
// 2) Fallback: scan the text
if (!candidates.length) {
  candidates = [...txt.matchAll(/https?:\/\/[^\s<>()]+/gi)].map(m => m[0]);
}

// 3) Sanitize + extract /in/<slug> (supports /m/in/ and /mwlite/in/)
const sanitize = s =>
  (s || '')
    .replace(/[\p{Z}\p{C}]+/gu, '')   // remove whitespace & control chars (incl. \n, NBSP, ZWSP)
    .replace(/[)\].,;!?]+$/, '');     // drop trailing punctuation

const IN_PROFILE = /linkedin\.com\/(?:mwlite\/|m\/)?in\/([A-Za-z0-9._%-]+)/i;

for (const raw0 of candidates) {
  const raw = sanitize(raw0);
  const m = raw.match(IN_PROFILE);
  if (!m) continue;

  const slug = decodeURIComponent(m[1]).replace(/\/+$/, '');
  if (!slug) continue;

  return [{
    json: {
      isLinkedIn: true,
      slug,
      possibleProfileUrl: `https://www.linkedin.com/in/${slug}`,
      sourceUrl: raw
    }
  }];
}

// Nothing matched
return [{ json: { isLinkedIn: false } }];
