const data = $input.all();

const flatData = data.length > 0 && Array.isArray(data[0].json) ? data[0].json : data.map(item => item.json);

const balanceSheet = flatData.filter(item => item.Date);
const debtSheet = flatData.filter(item => item.Person_Name);

const currentBalance = balanceSheet[balanceSheet.length - 1];

const activeDebts = debtSheet.filter(item => item.Status === "Active");

const totalActiveDebt = activeDebts.reduce((sum, debt) => sum + debt.Amount, 0);

let message = `Daily Finance Check - ${new Date().toISOString().split('T')[0]}\n`;
message += `Current Balance:\n`;
message += `  Online: ‚Çπ${currentBalance.Online_Balance}\n`;
message += `  Cash: ‚Çπ${currentBalance.Cash_Balance}\n`;
message += `  Total: ‚Çπ${currentBalance.Total_Balance}\n\n`;
message += `Active Debts:\n`;
message += `  They owe you: ‚Çπ${totalActiveDebt}\n`;

activeDebts.forEach(debt => {
  message += `    ‚Ä¢ ${debt.Person_Name}: ‚Çπ${debt.Amount}\n`;
});

message += `\nPlease share today's transactions:\n`;
message += `Example format:\n`;
message += `  - ‚Çπ500 cash travel\n`;
message += `  - ‚Çπ200 online food\n`;
message += `  - Borrowed ‚Çπ1000 from Rahul\n`;
message += `  - Lent ‚Çπ500 to Priya\n`;
message += `  - Rahul paid back ‚Çπ500\n\n`;
message += `Reply to this message with your transactions!`;

return {
  message: message,
  currentBalance: currentBalance,
  activeDebts: activeDebts,
  totalActiveDebt: totalActiveDebt
};
