// Character limit validation with accurate counting

const headline = $input.item.json.headline || '';
const description = $input.item.json.description || '';

// Function to accurately count displayed characters
function getCharacterCount(str) {
  str = str.trim();
  return str.length;
}

// Validate headline (max 30 characters)
let headlineResult = {};
const headlineCount = getCharacterCount(headline);

if (headlineCount > 30) {
  let truncated = headline.substring(0, 27).trim();
  truncated = truncated.replace(/[,.\-:]$/, '');
  headlineResult = {
    headline: truncated + '...',
    char_count: truncated.length + 3,
    truncated: true,
    original: headline
  };
} else {
  headlineResult = {
    headline: headline,
    char_count: headlineCount,
    truncated: false,
    original: headline
  };
}

// Validate description (max 90 characters)
let descriptionResult = {};
const descriptionCount = getCharacterCount(description);

if (descriptionCount > 90) {
  let truncated = description.substring(0, 87).trim();
  truncated = truncated.replace(/[,.\-:]$/, '');
  descriptionResult = {
    description: truncated + '...',
    char_count: truncated.length + 3,
    truncated: true,
    original: description
  };
} else {
  descriptionResult = {
    description: description,
    char_count: descriptionCount,
    truncated: false,
    original: description
  };
}

// Return validated data with original product info
return {
  product_id: $input.item.json.product_id || $input.item.json.id,
  product_title: $input.item.json.product_title || $input.item.json.title,
  validated_headline: headlineResult.headline,
  headline_char_count: headlineResult.char_count,
  headline_truncated: headlineResult.truncated,
  validated_description: descriptionResult.description,
  description_char_count: descriptionResult.char_count,
  description_truncated: descriptionResult.truncated,
  category: $input.item.json.category,
  final_url: $input.item.json.product_url || $input.item.json.link,
  validation_timestamp: new Date().toISOString()
};

