// Generate CSV from aggregated data

const items = $input.all();

// CSV headers
const headers = ['product_id', 'headline', 'description', 'final_url', 'display_url'];

// Create CSV rows
const rows = items.map(item => {
  const json = item.json;
  return [
    json.product_id || '',
    json.headline || '',
    json.description || '',
    json.final_url || '',
    json.display_url || ''
  ];
});

// Convert to CSV string
const csvRows = [headers, ...rows];
const csvString = csvRows
  .map(row => row.map(cell => `"${String(cell).replace(/"/g, '""')}"`).join(','))
  .join('\n');

// Return CSV data
return {
  csv_data: csvString,
  total_ads: rows.length,
  generated_at: new Date().toISOString(),
  filename: `google_ads_${new Date().toISOString().split('T')[0]}.csv`
};

