// Extract and structure the generated content
const generatedText = $input.first().json.output || $input.first().json.response || $input.first().json.text;
const requestData = $('Prepare Request Data').first().json;

// Word count
const wordCount = generatedText.split(/\s+/).length;

// Extract title (first line usually)
const lines = generatedText.split('\n').filter(line => line.trim());
const title = lines[0].replace(/^(Title:|#)\s*/i, '').trim();

// Calculate quality score (simple heuristic)
function calculateQualityScore(text) {
  let score = 70; // Base score

  // Length check
  const words = text.split(/\s+/).length;
  if (words >= requestData.wordTarget * 0.8 && words <= requestData.wordTarget * 1.2) {
    score += 10;
  }

  // Has subheadings
  if (text.match(/^#{1,3}\s|^\*\*.*\*\*$/m)) {
    score += 5;
  }

  // Has bullet points or numbered lists
  if (text.match(/^[-*]\s|^\d+\.\s/m)) {
    score += 5;
  }

  // Has CTA
  if (text.toLowerCase().includes('call to action') || text.toLowerCase().includes('cta:')) {
    score += 5;
  }

  // Not too repetitive (check for repeated phrases)
  const sentences = text.split(/[.!?]+/);
  const uniqueSentences = new Set(sentences);
  if (uniqueSentences.size / sentences.length > 0.8) {
    score += 5;
  }

  return Math.min(score, 100);
}

const qualityScore = calculateQualityScore(generatedText);

// Extract key stats
const stats = {
  wordCount: wordCount,
  paragraphCount: generatedText.split('\n\n').length,
  hasTitle: title.length > 0,
  hasCTA: generatedText.toLowerCase().includes('cta:') || generatedText.toLowerCase().includes('call to action'),
  qualityScore: qualityScore,
  readingTime: Math.ceil(wordCount / 200) // Average reading speed
};

return [{
  json: {
    ...requestData,
    aiDraft: generatedText,
    title: title,
    stats: stats,
    generatedAt: new Date().toISOString(),
    status: 'pending_review'
  }
}];
