// n8n Function Node: Manual JSON Parser & Validator
const raw = $input.first().json.text;

// Handle case where LLM returns a string (common if JSON is malformed)
let data;
if (typeof raw === 'string') {
  try {
    // Try to parse JSON, including handling common LLM formatting issues
    const cleanedRaw = raw
      .replace(/(\w)'(\w)/g, '$1\'$2') // Fix unescaped quotes in words
      .replace(/,\s*}/g, '}') // Remove trailing commas
      .replace(/,\s*]/g, ']'); // Remove trailing commas in arrays

    data = JSON.parse(cleanedRaw);
  } catch (e) {
    throw new Error(`LLM did not return valid JSON. Output: ${raw}`);
  }
} else if (typeof raw === 'object' && raw !== null) {
  // n8n already parsed it as JSON object
  data = { ...raw };
} else {
  throw new Error(`Unexpected LLM output type: ${typeof raw}`);
}

// --- Validation & Correction ---
const requiredFields = [
  'platform', 'post_text', 'suggested_hashtags',
  'cta_used', 'tone_applied', 'goal_applied',
  'audience_targeted', 'image_prompt'
];

// Check for missing fields - CRITICAL ERROR
for (const field of requiredFields) {
  if (!(field in data)) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Ensure post_text is string - CRITICAL ERROR
if (typeof data.post_text !== 'string') {
  throw new Error('post_text must be a string');
}

// Auto-correct character_count (most reliable approach!)
const actualCount = data.post_text.length;
// Optional: warn if LLM provided a different count
if (typeof data.character_count !== 'undefined' && data.character_count !== actualCount) {
  console.log(`âš ï¸ LLM reported character_count=${data.character_count}, but actual is ${actualCount}. Using actual.`);
}
data.character_count = actualCount;

// Validate and fix hashtags - AUTO-FIX MINOR ISSUES
if (!Array.isArray(data.suggested_hashtags)) {
  // Try to convert to array if it's a string
  if (typeof data.suggested_hashtags === 'string') {
    console.log('âš ï¸ suggested_hashtags was a string, converting to array');
    data.suggested_hashtags = data.suggested_hashtags.split(',').map(tag => tag.trim());
  } else {
    throw new Error('suggested_hashtags must be an array');
  }
}

// Fix hashtags by ensuring they all start with # and are valid
data.suggested_hashtags = data.suggested_hashtags
  .map(tag => {
    if (typeof tag !== 'string') {
      // Try to convert non-string values
      if (tag === null || tag === undefined) {
        console.log('âš ï¸ Found null/undefined hashtag, removing');
        return null;
      }
      console.log(`âš ï¸ Converting non-string hashtag: ${tag}`);
      tag = String(tag);
    }

    // Remove any existing # and add a new one to ensure proper formatting
    const cleanTag = tag.replace(/^#+/, '').trim();

    // Skip empty tags
    if (!cleanTag) {
      console.log('âš ï¸ Found empty hashtag, removing');
      return null;
    }

    // Add # prefix
    return `#${cleanTag}`;
  })
  .filter(tag => tag !== null) // Remove null entries
  .filter(tag => tag.length <= 30) // Remove overly long hashtags
  .slice(0, 30); // Limit to 30 hashtags maximum

// Ensure we still have some hashtags after cleaning
if (data.suggested_hashtags.length === 0) {
  console.log('âš ï¸ No valid hashtags found after cleaning, using fallback');
  data.suggested_hashtags = ['#socialmedia'];
}

// Ensure image_prompt is null or string - AUTO-FIX
if (data.image_prompt === undefined) {
  data.image_prompt = null;
} else if (data.image_prompt !== null && typeof data.image_prompt !== 'string') {
  console.log(`âš ï¸ Converting image_prompt from ${typeof data.image_prompt} to string`);
  data.image_prompt = String(data.image_prompt);
}

// Clean up string fields - AUTO-FIX MINOR ISSUES
const stringFields = ['post_text', 'cta_used', 'tone_applied', 'goal_applied', 'audience_targeted'];
stringFields.forEach(field => {
  if (data[field] && typeof data[field] !== 'string') {
    console.log(`âš ï¸ Converting ${field} from ${typeof data[field]} to string`);
    data[field] = String(data[field]);
  }

  // Trim whitespace from string fields
  if (typeof data[field] === 'string') {
    data[field] = data[field].trim();
  }
});

// Platform validation with case-insensitive matching - AUTO-FIX CASE ISSUES
const allowedPlatforms = [
  'facebook', 'instagram', 'linkedin', 'twitter', 
  'threads', 'tiktok', 'youtube', 'pinterest', 
  'reddit', 'bluesky'
];

// Convert platform to lowercase for comparison
const platformLower = data.platform.toLowerCase().trim();
const matchedPlatform = allowedPlatforms.find(p => p.toLowerCase() === platformLower);

if (!matchedPlatform) {
  throw new Error(`Unsupported platform: ${data.platform}. Allowed platforms: ${allowedPlatforms.join(', ')}`);
}

// Use the properly cased platform name
if (data.platform !== matchedPlatform) {
  console.log(`âš ï¸ Normalizing platform name from "${data.platform}" to "${matchedPlatform}"`);
  data.platform = matchedPlatform;
}

// Validate field lengths - WARN BUT DON'T ERROR
if (data.post_text.length > 280 && ['twitter', 'x'].includes(data.platform)) {
  console.log('âš ï¸ Post text exceeds 280 characters for Twitter/X platform');
}

if (data.post_text.length > 2000 && data.platform === 'linkedin') {
  console.log('âš ï¸ Post text exceeds 2000 characters for LinkedIn');
}

// Remove any extra fields that might cause issues
const allowedFields = [...requiredFields, 'character_count'];
Object.keys(data).forEach(key => {
  if (!allowedFields.includes(key)) {
    console.log(`âš ï¸ Removing unexpected field: ${key}`);
    delete data[key];
  }
});

// Final validation - CRITICAL CHECKS
if (data.post_text.trim().length === 0) {
  throw new Error('post_text cannot be empty after trimming');
}

if (data.suggested_hashtags.length === 0) {
  throw new Error('No valid hashtags remain after processing');
}

console.log('âœ… Validation completed successfully with auto-fixes applied');
return { json: data };

