const items = $input.all();
const seenPlatforms = new Set();
const uniqueItems = [];

items.forEach(item => {
  const platform = item.json.platform;

  // If we haven't seen this platform yet, add it to results
  if (platform && !seenPlatforms.has(platform)) {
    seenPlatforms.add(platform);
    uniqueItems.push(item);
  }
});

return uniqueItems;
