const data = $input.first().json;

const get = (path, fallback = null) => {
  try {
    return path.split('.').reduce((obj, key) => obj && obj[key], data) ?? fallback;
  } catch {
    return fallback;
  }
};
const lead = {
  "Record ID": get('vid'), 
  "First Name": get('properties.firstname.value'),
  "Last Name": get('properties.lastname.value'),
  "Email": get('properties.email.value'),
  "Phone Number": get('properties.phone.value'),
  "Description": get('properties.descriptions.value'),
  "Job Title": get('properties.jobtitle.value'),
  "Team Size": get('properties.team_size.value'),

  "Industry":
    get('properties.aIndustry.value') ||
    get('associated-company.properties.aIndustry.value') ||
    get('properties.industry.value') ||
    get('associated-company.properties.industry.value') ||
    get('properties.hs_industry.value') ||
    get('associated-company.properties.hs_industry.value') ||
    get('properties.aindustry.value'),

  "Company Website":
    get('associated-company.properties.website') ||
    get('properties.company_website.value'),

  "Company Name": get('associated-company.properties.name'),
};

const cleanLead = Object.fromEntries(
  Object.entries(lead).filter(([_, v]) => v && v !== 'null' && v !== 'undefined' && v !== '')
);

for (const key of Object.keys(lead)) {
  if (!cleanLead[key]) cleanLead[key] = "N/A";
}

return [{ json: cleanLead }];

