try {
  const inputData = $('Create Beat Inputs').item.json;

  // FFmpeg command: merge existing MP4 with new audio
  const command = `
ffmpeg -hide_banner -loglevel error -i "${inputData.fileName}" -i "${inputData.audioFileName}" \
-filter_complex "[1:a]atempo=1.3,apad=pad_dur=0.9[aud]" \
-map 0:v -map "[aud]" \
-c:v copy -c:a aac -b:a 192k -shortest "${inputData.videoFileName}" -y
  `;

  return {
    json: {
      command,
      inputVideo: inputData.fileName,
      inputAudio: inputData.audioFileName,
      outputVideo: inputData.videoFileName,
      timestamp: new Date().toISOString()
    }
  };
} catch (error) {
  return {
    json: {
      status: 'error',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  };
}

