const workflowSeed = Math.floor(Math.random() * 1000000);

// Get existing files from "Check For Already Created Beats" node
let existingFiles = [];
try {
  const searchResults = $('Check For Already Created Beats').all();
  if (searchResults && searchResults.length > 0) {
    existingFiles = searchResults.map(f => ({
      id: f.json.id,
      name: f.json.name
    }));
    console.log(`Found ${existingFiles.length} existing files`);
  } else {
    console.log('No existing files found, processing all beats');
  }
} catch (error) {
  console.log('Search returned no results, processing all beats');
}

// Get data from upstream nodes
const storyData = $('Get Story Idea').first().json;
const narrationData = $('Narration Prompt Generator').first().json.output.narratives;
const imageData = $('Image Prompt Generator').first().json.output;

// Collect all possible beats
const rawInputs = [
  { narrative: storyData['beat 1'], narrativeaudio: narrationData.narrative1 }, // FIXED: Added "1"
  { narrative: storyData['beat 2'], narrativeaudio: narrationData.narrative2 },
  { narrative: storyData['beat 3'], narrativeaudio: narrationData.narrative3 },
  { narrative: storyData['beat 4'], narrativeaudio: narrationData.narrative4 },
  { narrative: storyData['beat 5'], narrativeaudio: narrationData.narrative5 },
  { narrative: storyData['beat 6'], narrativeaudio: narrationData.narrative6 },
  { narrative: storyData['beat 7'], narrativeaudio: narrationData.narrative7 },
  { narrative: storyData['beat 8'], narrativeaudio: narrationData.narrative8 }
];

// Debug: Check what we got
console.log('Raw inputs count:', rawInputs.length);
console.log('Beat 1 check:', rawInputs[0].narrative ? 'OK' : 'MISSING');

// Filter out empty beats
const filteredInputs = rawInputs.filter(input => input.narrative && input.narrative.trim() !== "");
console.log(`Processing ${filteredInputs.length} non-empty beats`);

// Map filtered beats to output items
const outputItems = filteredInputs.map((textInput, index) => {
  const beatNumber = index + 1; // Start from 1 instead of 0
  const videoFileName = `horrorfile${beatNumber}_1.mp4`;

  // Check if file already exists (by name or id)
  const alreadyExists = existingFiles.some(file =>
    file.name === videoFileName || file.id === videoFileName
  );

  return {
    json: {
      ...textInput,
      index: beatNumber,
      seed: workflowSeed,
      character: imageData.character,
      negativeprompt: imageData.negative_prompt,
      fileName: `horrorfile${beatNumber}.mp4`,
      audioFileName: `horrorfile${beatNumber}.mp3`,
      videoFileName: videoFileName,
      skip: alreadyExists
    }
  };
});

// Optional log for skipped items
const skipped = outputItems.filter(item => item.json.skip).map(i => i.json.videoFileName);
if (skipped.length > 0) {
  console.log(`â­ï¸ Skipped existing files: ${skipped.join(', ')}`);
}

// Return only new items for downstream processing
const newItems = outputItems.filter(item => !item.json.skip);
console.log(`Outputting ${newItems.length} items for processing`);

return newItems;
