const attendanceData = $('Fetch Attendance Records').all();
const employeeData = $('Fetch Employee Master Data').all();
const now = new Date();
const today = now.toISOString().split('T')[0];
const currentHour = now.getHours();

const employeeMap = {};
employeeData.forEach(emp => {
  employeeMap[emp.json.EmployeeID] = {
    name: emp.json.EmployeeName,
    department: emp.json.Department,
    manager: emp.json.Manager,
    shift: emp.json.Shift || 'Day',
    email: emp.json.Email
  };
});

const todayRecords = attendanceData.filter(item => item.json.Date === today);

const statusCount = {
  present: 0,
  absent: 0,
  late: 0,
  leave: 0,
  wfh: 0
};

const statusLists = {
  late: [],
  absent: [],
  wfh: []
};

const deptMetrics = {};

todayRecords.forEach(record => {
  const empId = record.json.EmployeeID;
  const status = record.json.Status;
  const checkIn = record.json.CheckInTime;
  const employee = employeeMap[empId];

  if (!employee) return;

  const dept = employee.department;
  if (!deptMetrics[dept]) {
    deptMetrics[dept] = { present: 0, absent: 0, late: 0, total: 0 };
  }

  deptMetrics[dept].total++;

  switch(status) {
    case 'Present':
      statusCount.present++;
      deptMetrics[dept].present++;
      break;
    case 'Absent':
      statusCount.absent++;
      deptMetrics[dept].absent++;
      statusLists.absent.push({ name: employee.name, department: dept, manager: employee.manager });
      break;
    case 'Late':
      statusCount.late++;
      deptMetrics[dept].late++;
      statusLists.late.push({ name: employee.name, department: dept, checkIn: checkIn || 'N/A', lateBy: '15 min' });
      break;
    case 'Leave':
      statusCount.leave++;
      break;
    case 'WFH':
      statusCount.wfh++;
      statusLists.wfh.push({ name: employee.name, department: dept });
      break;
  }
});

const totalEmployees = Object.keys(employeeMap).length;
const activeEmployees = statusCount.present + statusCount.absent + statusCount.late;
const attendanceRate = activeEmployees > 0 ? ((statusCount.present + statusCount.late) / activeEmployees * 100).toFixed(2) : 0;
const punctualityRate = (statusCount.present + statusCount.late) > 0 ? (statusCount.present / (statusCount.present + statusCount.late) * 100).toFixed(2) : 0;

const alerts = [];
if (statusCount.late > totalEmployees * 0.1) {
  alerts.push({ type: 'warning', severity: 'medium', message: `High tardiness: ${statusCount.late} employees late`, action: 'Review shift timings' });
}

if (statusCount.absent > totalEmployees * 0.15) {
  alerts.push({ type: 'alert', severity: 'high', message: `High absence rate: ${statusCount.absent} employees absent`, action: 'Investigate issues' });
}

return {
  date: today,
  timestamp: now.toISOString(),
  hour: currentHour,
  totalEmployees: totalEmployees,
  recordsProcessed: todayRecords.length,
  present: statusCount.present,
  absent: statusCount.absent,
  late: statusCount.late,
  onLeave: statusCount.leave,
  wfh: statusCount.wfh,
  attendanceRate: parseFloat(attendanceRate),
  punctualityRate: parseFloat(punctualityRate),
  absenteeismRate: parseFloat(((statusCount.absent / totalEmployees) * 100).toFixed(2)),
  lateEmployees: statusLists.late,
  absentEmployees: statusLists.absent,
  wfhEmployees: statusLists.wfh,
  departmentBreakdown: deptMetrics,
  alerts: alerts,
  alertCount: alerts.length,
  hasHighPriorityAlerts: alerts.some(a => a.severity === 'high'),
  shouldNotifyManagement: alerts.length > 0 || statusCount.absent > 5
};

const data = $input.first().json;

const lateList = data.lateEmployees.length > 0 ? data.lateEmployees.slice(0, 5).map(e => `‚Ä¢ ${e.name} (${e.department})`).join('\n') : 'None';
const absentList = data.absentEmployees.length > 0 ? data.absentEmployees.slice(0, 5).map(e => `‚Ä¢ ${e.name} (${e.department})`).join('\n') : 'None';

const blocks = [
  {
    type: "header",
    text: { type: "plain_text", text: `üìä Attendance Report - ${data.date}`, emoji: true }
  },
  {
    type: "section",
    fields: [
      { type: "mrkdwn", text: `*‚úÖ Present*\n${data.present}` },
      { type: "mrkdwn", text: `*‚è∞ Late*\n${data.late}` },
      { type: "mrkdwn", text: `*‚ùå Absent*\n${data.absent}` },
      { type: "mrkdwn", text: `*üèñÔ∏è Leave*\n${data.onLeave}` }
    ]
  },
  { type: "divider" },
  {
    type: "section",
    text: { type: "mrkdwn", text: `*‚è∞ Late Arrivals*\n${lateList}` }
  },
  {
    type: "section",
    text: { type: "mrkdwn", text: `*‚ùå Absences*\n${absentList}` }
  }
];

return { ...data, slackBlocks: blocks };
