const taxCalendar = $('Fetch Tax Calendar').all();
const companyConfig = $('Fetch Company Config').first().json;
const now = new Date();
const today = now.toISOString().split('T')[0];

const getDaysDifference = (date1, date2) => {
  const diffTime = new Date(date1) - new Date(date2);
  return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
};

const jurisdictions = (companyConfig.Jurisdictions || 'Federal').split(',').map(j => j.trim());
const entityType = companyConfig.EntityType || 'Corporation';

const upcomingDeadlines = [];
const criticalDeadlines = [];
const overdueDeadlines = [];

taxCalendar.forEach(item => {
  const deadline = item.json;
  const deadlineDate = new Date(deadline.DeadlineDate);
  const daysUntil = getDaysDifference(deadlineDate, now);

  if (deadline.IsActive !== 'TRUE' && deadline.IsActive !== true) return;

  const jurisdictionMatch = !deadline.Jurisdiction || jurisdictions.includes(deadline.Jurisdiction);
  const entityMatch = !deadline.EntityType || deadline.EntityType === entityType || deadline.EntityType === 'All';

  if (!jurisdictionMatch || !entityMatch) return;

  const deadlineInfo = {
    id: deadline.DeadlineID || `TAX-${Date.now()}`,
    name: deadline.DeadlineName,
    description: deadline.Description || '',
    deadlineDate: deadline.DeadlineDate,
    daysUntil: daysUntil,
    jurisdiction: deadline.Jurisdiction || 'Federal',
    category: deadline.Category || 'General',
    assignedTo: deadline.AssignedTo || 'Finance Team',
    penalties: deadline.Penalties || 'Standard penalties',
    priority: 'Normal'
  };

  if (daysUntil < 0) {
    deadlineInfo.priority = 'Critical';
    overdueDeadlines.push(deadlineInfo);
  } else if (daysUntil <= 3) {
    deadlineInfo.priority = 'Critical';
    criticalDeadlines.push(deadlineInfo);
    upcomingDeadlines.push(deadlineInfo);
  } else if (daysUntil <= 7) {
    deadlineInfo.priority = 'High';
    upcomingDeadlines.push(deadlineInfo);
  } else if (daysUntil <= 30) {
    deadlineInfo.priority = 'Medium';
    upcomingDeadlines.push(deadlineInfo);
  }
});

return {
  date: today,
  timestamp: now.toISOString(),
  jurisdictions: jurisdictions.join(', '),
  entityType: entityType,
  totalUpcoming: upcomingDeadlines.length,
  criticalCount: criticalDeadlines.length,
  overdueCount: overdueDeadlines.length,
  next7Days: upcomingDeadlines.filter(d => d.daysUntil <= 7).length,
  upcomingDeadlines: upcomingDeadlines,
  criticalDeadlines: criticalDeadlines,
  overdueDeadlines: overdueDeadlines,
  requiresImmediateAction: overdueDeadlines.length > 0 || criticalDeadlines.length > 0,
  alertLevel: overdueDeadlines.length > 0 ? 'CRITICAL' : criticalDeadlines.length > 0 ? 'HIGH' : 'MEDIUM'
};

