const deadlineData = $('Analyze Deadlines').first().json;
const aiResponse = $input.first().json;

let aiInsights = 'AI analysis completed';
try {
  aiInsights = aiResponse.choices[0].message.content;
} catch (e) {
  aiInsights = 'AI analysis unavailable';
}

return {
  ...deadlineData,
  aiInsights: aiInsights
};

const data = $input.first().json;

const blocks = [
  {
    type: "header",
    text: {
      type: "plain_text",
      text: `Tax Alert - ${data.date}`,
      emoji: true
    }
  },
  {
    type: "section",
    fields: [
      { type: "mrkdwn", text: `*Overdue:* ${data.overdueCount}` },
      { type: "mrkdwn", text: `*Critical:* ${data.criticalCount}` },
      { type: "mrkdwn", text: `*Next 7 Days:* ${data.next7Days}` },
      { type: "mrkdwn", text: `*Total:* ${data.totalUpcoming}` }
    ]
  }
];

if (data.overdueDeadlines.length > 0) {
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*OVERDUE:*\n${data.overdueDeadlines.map(dl => `‚Ä¢ ${dl.name} - ${Math.abs(dl.daysUntil)} days overdue`).join('\n')}`
    }
  });
}

if (data.criticalDeadlines.length > 0) {
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*CRITICAL:*\n${data.criticalDeadlines.map(dl => `‚Ä¢ ${dl.name} - ${dl.daysUntil} days left`).join('\n')}`
    }
  });
}

return { ...data, slackBlocks: blocks };
