// Input: single item from "Fetch company data from CEIDG" node (CEIDG v3 API)
// Structure: { firmy: [ { nazwa, adresDzialalnosci:{...}, wlasciciel:{ nip, regon }, ... } ], ... }

const data = $input.item.json;

// Validation
if (!data || !Array.isArray(data.firmy) || data.firmy.length === 0) {
  throw new Error('No company found in CEIDG response');
}

// First company from the list
const company = data.firmy[0];

// Get companyId from HubSpot Trigger
const hubspotPayload = $item(0).$node["When NIP property changes"].json;
const companyId = hubspotPayload.companyid || hubspotPayload.objectId;

// CEIDG v3: address is in "adresDzialalnosci"
const addr = company.adresDzialalnosci || {};

const street = [
  addr.ulica,
  addr.budynek,
  addr.lokal ? `/${addr.lokal}` : ''
]
.filter(Boolean)
.join(' ');

// Owner ‚Üí NIP/REGON
const owner = company.wlasciciel || {};
const nip = owner.nip || '';
const regon = owner.regon || '';

// Return format ready for HubSpot Company ‚Üí Update
return {
  json: {
    companyId: companyId,
    properties: {
      name: company.nazwa || '',
      address: street || '',
      city: addr.miasto || '',
      zip: addr.kod || '',
      state: addr.wojewodztwo || '',
      country: 'Poland',
      phone: company.telefon || '',
      website: company.www || '',
      description: `Auto-populated from CEIDG. NIP: ${nip || '‚Äî'}, REGON: ${regon || '‚Äî'}. Start: ${company.dataRozpoczecia || 'N/A'}`,
    },
  },
};
