options.customCss:
/* Grok UI ‚Äî Dark, neon accents, soft glow */
:root {
  /* Typography */
  --font-family: 'Inter', system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, 'Apple Color Emoji', 'Segoe UI Emoji';
  --font-weight-normal: 500;
  --font-weight-bold: 700;
  --font-size-body: 13px;
  --font-size-label: 13px;
  --font-size-test-notice: 12px;
  --font-size-input: 14px;
  --font-size-header: 22px;
  --font-size-paragraph: 14px;
  --font-size-link: 13px;
  --font-size-error: 12px;
  --font-size-html-h1: 28px;
  --font-size-html-h2: 22px;
  --font-size-html-h3: 18px;
  --font-size-html-h4: 16px;
  --font-size-html-h5: 13px;
  --font-size-html-h6: 12px;
  --font-size-subheader: 13px;

  /* Colors ‚Äî Grok vibes */
  --color-background: #0b0e14;        /* page bg */
  --color-test-notice-text: #f5d48a;
  --color-test-notice-bg: #1a1410;
  --color-test-notice-border: #4a3a22;

  --color-card-bg: #11131a;            /* card bg */
  --color-card-border: #1f2430;        /* card border */
  --color-card-shadow: rgba(155, 135, 245, 0.12);

  --color-link: #b8a6ff;               /* links */
  --color-header: #eceff4;             /* headings */
  --color-label: #c9ced8;              /* form labels */
  --color-input-border: #2a3040;       /* inputs */
  --color-input-text: #e6e9f0;
  --color-focus-border: #9b87f5;       /* focus ring color */

  /* Accent + actions */
  --color-submit-btn-bg: linear-gradient(90deg, #6d5cff 0%, #b83aff 100%);
  --color-submit-btn-text: #0b0e14;
  --color-clear-button-bg: #2a3040;

  /* Status */
  --color-error: #ff5c7c;
  --color-required: #ff84d8;

  /* Content */
  --color-html-text: #c9ced8;
  --color-html-link: #b8a6ff;
  --color-header-subtext: #9aa3b2;

  /* Radii */
  --border-radius-card: 14px;
  --border-radius-input: 12px;
  --border-radius-clear-btn: 50%;
  --card-border-radius: 14px;

  /* Spacing */
  --padding-container-top: 28px;
  --padding-card: 24px;
  --padding-test-notice-vertical: 10px;
  --padding-test-notice-horizontal: 16px;
  --margin-bottom-card: 16px;
  --padding-form-input: 12px;
  --card-padding: 24px;
  --card-margin-bottom: 16px;

  /* Dimensions */
  --container-width: 520px; /* slightly wider for breathing room */
  --submit-btn-height: 46px;
  --checkbox-size: 18px;

  /* Effects */
  --box-shadow-card: 0 16px 48px 0 var(--color-card-shadow);
  --opacity-placeholder: 0.55;

  /* Extra Grok-y helpers */
  --gradient-accent: linear-gradient(90deg, #6d5cff 0%, #b83aff 100%);
  --focus-ring: 0 0 0 3px rgba(155, 135, 245, 0.35);
}

/* ===== Optional: polish the default n8n form elements ===== */

/* Page / container */
body {
  background: var(--color-background);
  color: var(--color-html-text);
  font-family: var(--font-family);
  font-weight: var(--font-weight-normal);
}

/* Cards */
.card,
.n8n-form-card {
  background: var(--color-card-bg);
  border: 1px solid var(--color-card-border);
  border-radius: var(--card-border-radius);
  box-shadow: var(--box-shadow-card);
}

/* Headings */
h1, h2, h3, h4, h5, h6 {
  color: var(--color-header);
  letter-spacing: 0.1px;
}

/* Labels */
label {
  color: var(--color-label);
  font-weight: 600;
}

/* Inputs */
input[type="text"],
input[type="email"],
input[type="number"],
input[type="url"],
input[type="tel"],
textarea,
select {
  background: #0f1218;
  color: var(--color-input-text);
  border: 1px solid var(--color-input-border);
  border-radius: var(--border-radius-input);
  padding: var(--padding-form-input);
  outline: none;
  transition: border-color 120ms ease, box-shadow 120ms ease, background 120ms ease;
}

input::placeholder,
textarea::placeholder {
  opacity: var(--opacity-placeholder);
}

/* Focus glow */
input:focus,
textarea:focus,
select:focus {
  border-color: var(--color-focus-border);
  box-shadow: var(--focus-ring);
}

/* Checkboxes / radios */
input[type="checkbox"],
input[type="radio"] {
  width: var(--checkbox-size);
  height: var(--checkbox-size);
  accent-color: #9b87f5;
  border-radius: 6px;
}

/* Submit button */
button[type="submit"],
.n8n-form-submit {
  background: var(--gradient-accent);
  color: var(--color-submit-btn-text);
  height: var(--submit-btn-height);
  border: 0;
  border-radius: 12px;
  font-weight: 700;
  letter-spacing: 0.2px;
  cursor: pointer;
  transition: transform 80ms ease, filter 120ms ease;
}
button[type="submit"]:hover,
.n8n-form-submit:hover {
  filter: brightness(1.06);
}
button[type="submit"]:active,
.n8n-form-submit:active {
  transform: translateY(1px);
}

/* Secondary / clear button */
.n8n-form-clear,
button[type="button"] {
  background: var(--color-clear-button-bg);
  color: #d4d7de;
  border: 1px solid var(--color-input-border);
  border-radius: 12px;
}

/* Links */
a {
  color: var(--color-html-link);
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
}

/* Test notice / banner */
.notice,
.n8n-form-test-notice {
  color: var(--color-test-notice-text);
  background: var(--color-test-notice-bg);
  border: 1px solid var(--color-test-notice-border);
  border-radius: 10px;
  padding: var(--padding-test-notice-vertical) var(--padding-test-notice-horizontal);
}

/* Error states */
.error,
input.error,
textarea.error,
select.error {
  border-color: var(--color-error) !important;
  box-shadow: 0 0 0 3px rgba(255, 92, 124, 0.25) !important;
}
.error-text {
  color: var(--color-error);
  font-size: var(--font-size-error);
}

/* Subtext */
.form-subtext,
.helper-text {
  color: var(--color-header-subtext);
}

/* Container width */
.form-container,
.n8n-form-container {
  max-width: var(--container-width);
}
