// 0. Parse the incoming Document data
let raw = $input.first().json['Document data'];
let arr = typeof raw === 'string' ? JSON.parse(raw) : raw;

// If document name exists, use it ‚Äî else fallback
let source = $input.first().json['Document name'] || 'unknown_source';

// If your workflow previously had a reference like $('Insert Document Metadata1')
// and it caused an "unexecuted" error, we replace it with a safe null or fetch from input
let source_id = $input.first().json['source_metadata_id'] || $input.first().json['source'] || $input.first().json[' source'] || null;

// Helper: return character length of an array object
function getObjLength(obj) {
  return (obj.content || '').length;
}

function getObjLengthofTextOnly(obj) {
  // Ignore OCR images for character count
  if (obj.type === 'image_ocr') return 0;
  return (obj.content || '').length;
}

// Split any single item whose content is > chunkSize into multiple items
function splitOversizedItems(arr, chunkSize) {
  const result = [];
  for (const item of arr) {
    const content = item.content || '';
    if (content.length <= chunkSize) {
      result.push(item);
    } else {
      for (let start = 0; start < content.length; start += chunkSize) {
        const part = content.slice(start, start + chunkSize);
        result.push({ ...item, content: part });
      }
    }
  }
  return result;
}

// Chunking function
function chunkByCharLength(arr, source, chunkSize = 1000) {
  const flat = splitOversizedItems(arr, chunkSize);
  const response = [];
  let idx = 0;
  let charPos = 0;

  while (idx < flat.length) {
    const a = [];
    let sum = 0;
    const from = charPos;

    while (idx < flat.length && sum < chunkSize) {
      const item = flat[idx];
      const len = getObjLength(item);
      const text_len = getObjLengthofTextOnly(item);
      a.push(item);
      sum += len;
      charPos += text_len;
      idx++;
    }

    const to = charPos;
    const metadata = {
      source: source,
      source_metadata_id: source_id,
      loc: { Characters: { from, to } }
    };

    response.push({
      content: JSON.stringify({
        pageContent: a,
        metadata
      })
    });
  }
  return response;
}

// 1. Run chunking
const chunks = chunkByCharLength(arr, source, 1000);

// 2. Return in n8n-compatible format
return chunks.map(c => ({ json: c }));
