const deals = $input.all().map(i => i.json);

const cleaned = deals.map(d => ({
  deal_id: d.id,
  deal_name: d.name,
  rep_id: d.assignedTo || "Unassigned",
  status: d.status,
  stage_id: d.pipelineStageId,
  value: d.monetaryValue || 0,
  client_name: d.relations?.[0]?.contactName || "Unknown",
  company_name: d.relations?.[0]?.companyName || "",
  updated_at: d.updatedAt
}));

return cleaned.map(c => ({ json: c }));

const deals = $input.all().map(i => i.json);

// Object to hold totals per rep
const summary = {};

for (const d of deals) {
  const rep = d.rep_id || "Unassigned";
  if (!summary[rep]) {
    summary[rep] = {
      rep_id: rep,
      total_deals: 0,
      total_value: 0,
      won_deals: 0,
    };
  }

  summary[rep].total_deals += 1;
  summary[rep].total_value += d.value || 0;

  // If your GHL marks won deals as 'won' or similar
  if (d.status && d.status.toLowerCase().includes("won")) {
    summary[rep].won_deals += 1;
  }
}

// Convert to array for output
return Object.values(summary).map(rep => ({
  json: {
    rep_id: rep.rep_id,
    total_deals: rep.total_deals,
    total_value: rep.total_value,
    won_deals: rep.won_deals,
    avg_value: rep.total_value / rep.total_deals,
  },
}));

