// Get all contacts from the input
const contacts = $input.all().map(item => item.json);

// Get the current date and time
const now = new Date();

// Filter contacts with no response in the last 24 hours
const contactsWithNoResponse = contacts.filter(contact => {
  const lastResponseTime = new Date(contact.dateUpdated);

  // Calculate the time difference in hours
  const timeDiff = now - lastResponseTime; 
  const timeDiffInHours = timeDiff / (1000 * 60 * 60);

  // Check if no response in the last 24 hours
  return timeDiffInHours >= 24; 
});

// Return the filtered contacts as separate items
return contactsWithNoResponse.map(contact => ({
  json: contact
}));

// Get all contacts from the input
const contacts = $input.all().map(item => item.json);

// Sort contacts by dateUpdated (most recent first)
const sortedContacts = contacts.sort((a, b) => {
  return new Date(b.dateUpdated) - new Date(a.dateUpdated);
});

// Get the most recent contact
const mostRecentContact = sortedContacts[0];

// Calculate hours ago
const now = new Date();
const lastUpdated = new Date(mostRecentContact.dateUpdated);
const timeDiff = now - lastUpdated;
const hoursAgo = Math.floor(timeDiff / (1000 * 60 * 60));

// Format the output
const formattedOutput = `${mostRecentContact.contactName} (${mostRecentContact.email || 'no email'}) - \`dateUpdated: "${mostRecentContact.dateUpdated}"\`
* That's ~${hoursAgo} hours ago ‚úÖ`;

// Return the formatted output
return [
  {
    json: {
      mostRecentContact: mostRecentContact,
      formattedOutput: formattedOutput,
      hoursAgo: hoursAgo,
      contactName: mostRecentContact.contactName,
      email: mostRecentContact.email,
      dateUpdated: mostRecentContact.dateUpdated
    }
  }
];

