(() => {
  const items = $input.all();

  const arr = items
    .map(i => i.json?.output ?? i.json)
    .filter(Boolean);

  const lines = arr.map(e => {
    const item = (e?.Item ?? '').toString().trim() || '‚Äî';
    const amount = (e?.Amount ?? '').toString().trim() || '‚Äî';
    const date = e?.Date ? ` (${e.Date})` : '';
    return `‚Ä¢ ${item} ‚Äî ${amount}${date}`;
  });

  const text =
    'üìí Bookkeeping Complete\n\n' +
    (lines.length ? lines.join('\n') : '(No items found)');

  // Telegram MarkdownV2 ÈúÄË¶ÅËΩ¨‰πâÁöÑÂ≠óÁ¨¶Ôºö
  // _ * [ ] ( ) ~ ` > # + - = | { } . ! \
  const escapeMdV2 = (s) =>
    s.replace(/([_*[\]()~`>#+\-=|{}.!\\])/g, '\\$1');

  return escapeMdV2(text);
})()
}}
