// Configure your brand monitoring keywords
const brandKeywords = [
  "YourBrandName",
  "your-product-name",
  "industry-keyword"
];

// Return array of keywords to search
return brandKeywords.map(keyword => ({
  json: {
    brandKeyword: keyword
  }
}));

// Aggregate all processed items
const items = $input.all();

const totalPosts = items.length;
const postsEngaged = items.filter(i => i.json.commentPosted === 'Yes').length;
const sentimentBreakdown = items.reduce((acc, item) => {
  const sentiment = item.json.sentiment || 'unknown';
  acc[sentiment] = (acc[sentiment] || 0) + 1;
  return acc;
}, {});

const avgScore = items.reduce((sum, item) => sum + (item.json.engagementScore || 0), 0) / totalPosts;

const topPosts = items
  .sort((a, b) => (b.json.engagementScore || 0) - (a.json.engagementScore || 0))
  .slice(0, 5)
  .map(item => ({
    title: item.json.postTitle,
    subreddit: item.json.subreddit,
    score: item.json.engagementScore,
    url: item.json.postUrl
  }));

return [{
  json: {
    summary: {
      totalPosts,
      postsEngaged,
      engagementRate: `${((postsEngaged / totalPosts) * 100).toFixed(1)}%`,
      avgEngagementScore: avgScore.toFixed(1),
      sentimentBreakdown,
      topPosts,
      reportDate: new Date().toISOString().split('T')[0]
    }
  }
}];

