const channel = eventCtx.channel;

return [{
  json: {
    textToTranslate,
    channel,
    thread_ts
  }
}];

/* /trans Áî®„Éï„Ç©„Éº„Éû„ÉÉ„Çø
   Âá∫Âäõ„Ç§„É°„Éº„Ç∏:
   „Äêfrom @ÂæåËó§„Äë\n(Êó•Êú¨Ë™ûÂéüÊñá...)\n----------\n(Ëã±Ë™ûË®≥...)
*/
const ctx = $node['Parse Slash Payload'].json;
const llm = $json;

let output = llm.text || llm.output || '';

// [Original] „Å® [Translation] „ÇíÂàÜÈõ¢
let original = '';
let translation = '';
const match = output.match(/\[Original\]([\s\S]*?)\[Translation\]([\s\S]*)/i);
if (match) {
  original = match[1].trim();
  translation = match[2].trim();
} else {
  translation = output.trim();
}

const fromUser = ctx.user_id ? `<@${ctx.user_id}>` : (ctx.user_name || 'someone');
const finalMessage = `„Äêfrom ${fromUser}„Äë\n${original}\n----------\n${translation}`;

return [{
  json: {
    response_url: ctx.response_url,
    message: finalMessage
  }
}];

