const allowedPatterns = ['jp', 'us'];
const reaction = ($json.reaction || '').toLowerCase();

// ÈÉ®ÂàÜ‰∏ÄËá¥„ÉÅ„Çß„ÉÉ„ÇØ
const isAllowed = allowedPatterns.some(p => reaction.includes(p));

