// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.type_hr = $('For each DNS type').first().json.Types;
}

return $input.all();

// Initialize variables to aggregate data across all input items
let domain = null;
const records = [];

// Loop over input items to process and aggregate the data
for (const item of $input.all()) {
  const itemJson = item.json;

  // 1. Get the domain name from the first item's Question array
  // Check if domain is not yet set and Question array exists
  if (domain === null && itemJson.Question && itemJson.Question.length > 0) {
    // Extract domain and remove the trailing dot if present
    domain = itemJson.Question[0].name.replace(/\.$/, '');
  }

  // Get the human-readable type from the current item
  // Fallback to a placeholder if 'type_hr' is missing
  const recordType = itemJson.type_hr || 'UNKNOWN';

  // 2. Process all answers or insert "NO ANSWER" placeholder
  const answerRecords = itemJson.Answer;

  if (answerRecords && answerRecords.length > 0) {
    // SUCCESS: Loop over existing answer records
    for (const record of answerRecords) {
      // Add the new record object using the top-level type_hr
      records.push({
        type: recordType,
        data: record.data
      });
    }
  } else {
    // FAILURE: Insert "NO ANSWER" placeholder for the current type
    records.push({
      type: recordType,
      data: "NO ANSWER"
    });
  }
}

// 3. Construct the final output object in the desired n8n format

// Create a new item that contains the aggregated data
const outputItem = {
    json: {
        domain: domain,
        records: records
    }
};

// Return a new array containing only the single aggregated output item
return [outputItem];
