// Initialize backup tracking
const databases = $input.all();
const backupStartTime = new Date().toISOString();
const folderId = $('Create Backup Folder').first().json.id;
const folderName = $('Create Backup Folder').first().json.name;

// Prepare databases with tracking info
const processedDatabases = databases.map((item, index) => {
  const db = item.json;
  const dbName = db.title?.[0]?.plain_text || `Database_${index + 1}`;

  return {
    json: {
      database_id: db.id,
      database_name: dbName,
      database_title: db.title,
      created_time: db.created_time,
      last_edited_time: db.last_edited_time,
      backup_folder_id: folderId,
      backup_folder_name: folderName,
      backup_start_time: backupStartTime,
      index: index + 1,
      total_databases: databases.length,
      status: 'pending'
    }
  };
});

return processedDatabases;

// Get database info from loop
const dbInfo = $('Loop Databases').first().json;
const dbName = dbInfo.database_name;
const dbId = dbInfo.database_id;
const backupTime = new Date().toISOString();

// Check if pages were retrieved successfully
const pagesNode = $input.first();
let pages = [];
let status = 'success';
let errorMessage = null;

try {
  if (pagesNode.error) {
    status = 'error';
    errorMessage = pagesNode.error.message || 'Unknown error';
  } else {
    pages = $input.all().map(item => item.json);
  }
} catch (error) {
  status = 'error';
  errorMessage = error.message;
}

// Create backup structure
const backup = {
  metadata: {
    database_id: dbId,
    database_name: dbName,
    database_title: dbInfo.database_title,
    backup_timestamp: backupTime,
    backup_date: new Date().toISOString().split('T')[0],
    total_pages: pages.length,
    created_time: dbInfo.created_time,
    last_edited_time: dbInfo.last_edited_time,
    backup_status: status,
    error_message: errorMessage
  },
  pages: pages
};

// Create safe filename
const safeFileName = dbName.replace(/[^a-z0-9]/gi, '_').toLowerCase();
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').split('T')[0];
const fileName = `${safeFileName}_${timestamp}.json`;

// Convert to JSON and then to binary
const jsonContent = JSON.stringify(backup, null, 2);
const base64Content = Buffer.from(jsonContent, 'utf8').toString('base64');

return {
  json: {
    database_name: dbName,
    database_id: dbId,
    file_name: fileName,
    pages_count: pages.length,
    backup_size_bytes: Buffer.byteLength(jsonContent, 'utf8'),
    status: status,
    error_message: errorMessage,
    backup_timestamp: backupTime
  },
  binary: {
    data: {
      data: base64Content,
      mimeType: 'application/json',
      fileName: fileName
    }
  }
};

// Collect all backup results
const allBackups = $input.all();
const config = $('Configuration Settings').first().json;
const folderInfo = $('Create Backup Folder').first().json;

// Calculate statistics
const successfulBackups = allBackups.filter(b => b.json.status === 'success');
const failedBackups = allBackups.filter(b => b.json.status === 'error');
const totalPages = successfulBackups.reduce((sum, b) => sum + b.json.pages_count, 0);
const totalSize = allBackups.reduce((sum, b) => sum + (b.json.backup_size_bytes || 0), 0);

// Format size
const formatSize = (bytes) => {
  if (bytes < 1024) return bytes + ' B';
  if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(2) + ' KB';
  return (bytes / (1024 * 1024)).toFixed(2) + ' MB';
};

// Create metadata
const metadata = {
  backup_info: {
    backup_date: new Date().toISOString().split('T')[0],
    backup_timestamp: new Date().toISOString(),
    folder_name: folderInfo.name,
    folder_id: folderInfo.id,
    folder_url: folderInfo.webViewLink
  },
  summary: {
    total_databases: allBackups.length,
    successful_backups: successfulBackups.length,
    failed_backups: failedBackups.length,
    total_pages_backed_up: totalPages,
    total_size: formatSize(totalSize),
    total_size_bytes: totalSize
  },
  successful_databases: successfulBackups.map(b => ({
    name: b.json.database_name,
    id: b.json.database_id,
    pages_count: b.json.pages_count,
    file_name: b.json.file_name,
    size: formatSize(b.json.backup_size_bytes)
  })),
  failed_databases: failedBackups.map(b => ({
    name: b.json.database_name,
    id: b.json.database_id,
    error: b.json.error_message
  })),
  configuration: {
    retention_days: config.retention_days,
    backup_format: config.backup_format,
    cleanup_enabled: config.enable_cleanup
  }
};

// Create metadata file
const metadataJson = JSON.stringify(metadata, null, 2);
const base64Metadata = Buffer.from(metadataJson, 'utf8').toString('base64');

return {
  json: {
    ...metadata.summary,
    folder_url: folderInfo.webViewLink,
    successful_databases: successfulBackups.map(b => b.json.database_name),
    failed_databases: failedBackups.map(b => b.json.database_name),
    has_failures: failedBackups.length > 0
  },
  binary: {
    data: {
      data: base64Metadata,
      mimeType: 'application/json',
      fileName: 'backup_metadata.json'
    }
  }
};

// Get retention days from config
const retentionDays = $('Configuration Settings').first().json.retention_days;
const currentFolderId = $('Create Backup Folder').first().json.id;

// Get all backup folders
const allFolders = $input.all();

// Si no hay carpetas, retornar false flag
if (!allFolders || allFolders.length === 0) {
  console.log('No folders received from previous node');
  return [{
    json: {
      has_folders: false,
      message: 'No backup folders found'
    }
  }];
}

// Calculate cutoff date
const cutoffDate = new Date();
cutoffDate.setDate(cutoffDate.getDate() - retentionDays);

console.log('=== DEBUG FILTER ===');
console.log('Retention days:', retentionDays);
console.log('Cutoff date:', cutoffDate.toISOString());
console.log('Current folder ID:', currentFolderId);
console.log('Total folders found:', allFolders.length);

// Filter folders older than retention period
const foldersToDelete = allFolders.filter(item => {
  const folder = item.json;

  // Don't delete the current backup
  if (folder.id === currentFolderId) {
    console.log('Skipping current backup:', folder.name);
    return false;
  }

  // Parse folder name to get date
  // Match both formats: Notion_Backup_2025-01-15 and Notion_Backup_V2_2025-01-15
  const match = folder.name.match(/Notion_Backup(?:_V\d+)?_(\d{4}-\d{2}-\d{2})/);

  if (!match) {
    console.log('No date match for:', folder.name);
    return false;
  }

  const folderDate = new Date(match[1]);
  const shouldDelete = folderDate < cutoffDate;

  console.log(`Folder: ${folder.name}, Date: ${match[1]}, Should delete: ${shouldDelete}`);

  return shouldDelete;
});

console.log('Folders to delete:', foldersToDelete.length);

// Si no hay carpetas para eliminar, retornar false flag
if (foldersToDelete.length === 0) {
  console.log('No folders to delete');
  return [{
    json: {
      has_folders: false,
      message: 'No old folders to delete'
    }
  }];
}

// Si hay carpetas para eliminar, agregarles el flag
return foldersToDelete.map(item => ({
  json: {
    ...item.json,
    has_folders: true
  }
}));

// Get summary data
const summary = $('Generate Metadata').first().json;
const config = $('Configuration Settings').first().json;

// Check if cleanup ran and if there were deletions
let cleanupInfo = '';
if (config.enable_cleanup) {
  try {
    // Try to access Delete Old Backup node
    const deletedFolders = $('Delete Old Backup').all();

    if (deletedFolders && deletedFolders.length > 0) {
      // Check if it actually deleted folders (not just the false flag)
      const actualDeletions = deletedFolders.filter(item => item.json.has_folders !== false);
      if (actualDeletions.length > 0) {
        cleanupInfo = `\nüóëÔ∏è *Cleanup:* ${actualDeletions.length} old backup(s) deleted (>${config.retention_days} days)`;
      } else {
        cleanupInfo = `\n‚úÖ *Cleanup:* No old backups to delete`;
      }
    } else {
      cleanupInfo = `\n‚úÖ *Cleanup:* No old backups to delete`;
    }
  } catch (error) {
    // Node wasn't executed (no old folders to delete)
    cleanupInfo = `\n‚úÖ *Cleanup:* No old backups to delete`;
  }
} else {
  cleanupInfo = '';
}

// Build status emoji
const statusEmoji = summary.has_failures ? '‚ö†Ô∏è' : '‚úÖ';

// Build message
let message = `${statusEmoji} *Notion Backup Complete*\n\n`;
message += `üìä *Summary:*\n`;
message += `‚Ä¢ Total Databases: ${summary.total_databases}\n`;
message += `‚Ä¢ Successful: ${summary.successful_backups} ‚úÖ\n`;

if (summary.failed_backups > 0) {
  message += `‚Ä¢ Failed: ${summary.failed_backups} ‚ùå\n`;
}

message += `‚Ä¢ Total Pages: ${summary.total_pages_backed_up}\n`;
message += `‚Ä¢ Total Size: ${summary.total_size}\n`;
message += cleanupInfo;
message += `\nüìÅ [Open Backup Folder](${summary.folder_url})`;

if (summary.has_failures) {
  message += `\n\n‚ùå *Failed Databases:*\n`;
  summary.failed_databases.forEach(db => {
    message += `‚Ä¢ ${db}\n`;
  });
}

return {
  message: message,
  chat_id: config.telegram_chat_id,
  parse_mode: 'Markdown'
};
