const MAX_LEN = 0; 

function toArrayMaybe(json) {
  if (Array.isArray(json)) return json;
  if (Array.isArray(json?.comments)) return json.comments;
  if (typeof json === 'string') {
    try {
      const parsed = JSON.parse(json);
      if (Array.isArray(parsed)) return parsed;
      if (Array.isArray(parsed?.comments)) return parsed.comments;
    } catch (_) {}
  }
  if (typeof json?.data === 'string') {
    try {
      const parsed = JSON.parse(json.data);
      if (Array.isArray(parsed)) return parsed;
    } catch (_) {}
  }
  return null;
}

function getIncomingComments(items) {
  const looksLikeSingleComment =
    items.length > 1 ||
    (items[0]?.json && items[0].json['@type'] === '@reddit_comment');
  if (looksLikeSingleComment) {
    return items
      .map(i => i.json)
      .filter(v => v && typeof v === 'object');
  }
  const payload = items[0]?.json;
  const arr = toArrayMaybe(payload);
  if (arr) return arr;
  return [];
}

function formatDate(unix) {
  if (!unix && unix !== 0) return null;
  const date = new Date(unix * 1000);
  return date.toLocaleString('ru-RU', {
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit'
  });
}

function transformComment(comment) {
  if (!comment || comment.is_deleted || !comment.text) return null;
  return {
    author: comment.author?.name || '[deleted]',
    created_at_unix: comment.created_at,
    created_at: formatDate(comment.created_at),
    text: comment.text,
    replies: (comment.replies || [])
      .map(transformComment)
      .filter(Boolean),
  };
}

function stringifySafe(obj) {
  const s = JSON.stringify(obj);
  if (MAX_LEN > 0 && s.length > MAX_LEN) {
    return s.slice(0, MAX_LEN - 3) + '...';
  }
  return s;
}

const allComments = getIncomingComments(items);

const topLevel = allComments.filter(
  c => c && typeof c.parent_id === 'string' && c.parent_id.startsWith('t3_')
);

const byPost = {};
for (const c of topLevel) {
  const postId = c.parent_id;
  const t = transformComment(c);
  if (!t) continue;
  if (!byPost[postId]) byPost[postId] = [];
  byPost[postId].push(t);
}

const out = Object.entries(byPost).map(([post_id, comments]) => {
  return {
    json: {
      post_id,
      comments_count: comments.length,
      comments_json: stringifySafe(comments)
    }
  };
});

return out.length ? out : [{ json: { post_id: null, comments_count: 0, comments_json: '[]' } }];

const MAX_LEN = 0;

function toArrayMaybe(json) {
  if (Array.isArray(json)) return json;
  if (Array.isArray(json?.comments)) return json.comments;
  if (typeof json === 'string') {
    try {
      const parsed = JSON.parse(json);
      if (Array.isArray(parsed)) return parsed;
      if (Array.isArray(parsed?.comments)) return parsed.comments;
    } catch (_) {}
  }
  if (typeof json?.data === 'string') {
    try {
      const parsed = JSON.parse(json.data);
      if (Array.isArray(parsed)) return parsed;
    } catch (_) {}
  }
  return null;
}

function getIncoming(items) {
  const looksLikeFlat =
    items.length > 1 ||
    (items[0]?.json && String(items[0].json['@type'] || '').includes('@linkedin_post_comment'));
  if (looksLikeFlat) {
    return items.map(i => i.json).filter(v => v && typeof v === 'object');
  }
  const payload = items[0]?.json;
  const arr = toArrayMaybe(payload);
  if (arr) return arr;
  return [];
}

function formatDateSmart(ts) {
  if (ts == null) return null;
  const isMs = Math.abs(ts) > 1e12;
  const d = new Date((isMs ? ts : ts * 1000));
  return d.toLocaleString('ru-RU', {
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit'
  });
}

function extractPostIdFromUrnValue(val) {
  if (typeof val !== 'string') return null;
  const m = val.match(/(?:activity|ugcPost):(\d+)/);
  return m ? m[1] : null;
}

function extractPostIdAny(c) {
  let id = extractPostIdFromUrnValue(c?.urn?.value);
  if (id) return id;
  id = extractPostIdFromUrnValue(c?.parent?.value);
  if (id) return id;
  id = extractPostIdFromUrnValue(c?.url);
  if (id) return id;
  try {
    const decoded = decodeURIComponent(c?.url || '');
    id = extractPostIdFromUrnValue(decoded);
    if (id) return id;
  } catch (_) {}
  return null;
}

function stringifySafe(obj) {
  const s = JSON.stringify(obj);
  if (MAX_LEN > 0 && s.length > MAX_LEN) return s.slice(0, MAX_LEN - 3) + '...';
  return s;
}

function transformLinkedInComment(c) {
  if (!c || !c.text) return null;
  const post_id = extractPostIdAny(c) || 'unknown_post';
  const created_at_raw = c.created_at ?? null;
  return {
    post_id,
    author: c.author?.name || '[unknown]',
    author_url: c.author?.url || null,
    text: c.text,
    created_at_raw,
    created_at: formatDateSmart(created_at_raw),
    reactions: Array.isArray(c.reactions) ? c.reactions : null,
    is_commenter_post_author: !!c.is_commenter_post_author,
    replies: [],
  };
}

const raw = getIncoming(items);
const normalized = raw.map(transformLinkedInComment).filter(Boolean);

const byPost = {};
for (const t of normalized) {
  const key = t.post_id || 'unknown_post';
  if (!byPost[key]) byPost[key] = [];
  byPost[key].push({
    author: t.author,
    author_url: t.author_url,
    text: t.text,
    created_at_raw: t.created_at_raw,
    created_at: t.created_at,
    reactions: t.reactions,
    is_commenter_post_author: t.is_commenter_post_author,
    replies: t.replies,
  });
}

const out = Object.entries(byPost).map(([post_id, comments]) => ({
  json: {
    post_id,
    comments_count: comments.length,
    comments_json: stringifySafe(comments)
  }
}));

return out.length ? out : [{ json: { post_id: null, comments_count: 0, comments_json: '[]' } }];

const FILTER_TYPES = null;
const MAX_LEN = 0;

function toArrayMaybe(json) {
  if (Array.isArray(json)) return json;
  if (typeof json === 'string') {
    try {
      const parsed = JSON.parse(json);
      if (Array.isArray(parsed)) return parsed;
    } catch (_) {}
  }
  if (Array.isArray(json?.data)) return json.data;
  if (typeof json?.data === 'string') {
    try {
      const parsed = JSON.parse(json.data);
      if (Array.isArray(parsed)) return parsed;
    } catch (_) {}
  }
  return null;
}

function getIncoming(items) {
  if (items.length > 1 && items.every(i => i?.json && typeof i.json === 'object')) {
    return items.map(i => i.json);
  }
  const payload = items[0]?.json;
  const arr = toArrayMaybe(payload);
  if (arr) return arr;
  if (payload && typeof payload === 'object') return [payload];
  return [];
}

function cutIfNeeded(str) {
  if (MAX_LEN > 0 && str.length > MAX_LEN) {
    return str.slice(0, MAX_LEN - 3) + '...';
  }
  return str;
}

const posts = getIncoming(items);

const filtered = Array.isArray(FILTER_TYPES)
  ? posts.filter(p => FILTER_TYPES.includes(p.type))
  : posts;

const idsUnique = Array.from(
  new Set(
    filtered
      .map(p => String(p.post_id ?? '').trim())
      .filter(v => v.length > 0)
  )
);

const post_ids_str = cutIfNeeded(idsUnique.join(','));

return [{
  json: {
    total: idsUnique.length,
    post_ids: idsUnique,
    post_ids_str
  }
}];
