// n8n Code node (JavaScript)

const DEFAULT_USER_ID = 'QLOTR0qfCRK9TMKR9jHs';
// ======================

function extractContent(obj) {
  // Handles several possible shapes:
  // 1) { message: { content: "..." } }
  // 2) { result: [ { message: { content: "..." } }, ... ] }
  // 3) [ { message: { content: "..." } }, ... ] directly in json
  if (!obj) return null;

  if (obj.message?.content) return obj.message.content;

  if (Array.isArray(obj.result) && obj.result[0]?.message?.content) {
    return obj.result[0].message.content;
  }

  if (Array.isArray(obj) && obj[0]?.message?.content) {
    return obj[0].message.content;
  }

  return null;
}

function normalizeBody(text) {
  if (typeof text !== 'string') return '';
  // Convert Windows line breaks, remove trailing double-space markdown breaks,
  // collapse excessive blank lines, and trim edges.
  return text
    .replace(/\r\n/g, '\n')
    .replace(/[ \t]+\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .trim();
}

const out = [];

for (const item of items) {
  const j = item.json ?? {};

  // Try multiple places to find the AI message content
  const content =
    extractContent(j) ??
    extractContent(j.data) ??          // sometimes nested
    extractContent(j.output) ??
    null;

  const body = normalizeBody(content);

  // Choose userId: prefer one passed in via the item (e.g., from a Set node), else default
  const userId = j.userId || DEFAULT_USER_ID;

  if (!userId) {
    throw new Error('userId is required. Set DEFAULT_USER_ID or pass userId in the incoming item.');
  }
  if (!body) {
    throw new Error('No note content found from the AI node output.');
  }

  // Output one item per incoming item, ready for an HTTP Request node (JSON body)
  out.push({
    json: {
      userId,
      body,
    },
  });
}

return out;
