parameters.jsonOutput:
={{
  (() => {
    const raw =
      $json.emotion ||
      (JSON.parse($json.output || '{}').emotion) ||
      'music';
    const clean = String(raw).replace(/\s+/g,' ').replace(/\u3000/g,' ').trim();
    const base = 'https://news.google.com/rss/search?hl=en-US&gl=US&ceid=US%3Aen&q=';
    return { feedUrl: base + encodeURIComponent(clean) }; // ‚Üê „Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„ÅßËøî„Åô
  })()
}}

parameters.url:
={{
  (() => {
    const emotionRaw =
      $json.emotion ||
      (JSON.parse($json.output || '{}').emotion) ||
      'music';

    const q = encodeURIComponent(String(emotionRaw).trim());
    const url = `https://news.google.com/rss/search?hl=en-US&gl=US&ceid=US%3Aen&q=${q}`;
    return url.trim();
  })()
}}

