// This array will hold the final output items
const outputItems = [];

// Loop through each item provided to the node
for (const item of items) {
  // Get the description string from the input JSON
  const description = item.json.description;

  // Initialize variables to store the results
  let name = null;
  let email = null;
  let websiteUrl = null;

  // --- ATTEMPT 1: Parse for labeled data (e.g., "Name: ...") ---
  const nameMatch = description.match(/name:\s*(.*)/i);
  if (nameMatch && nameMatch[1]) {
    name = nameMatch[1].trim();
  }

  const emailMatch = description.match(/email:\s*\[?([\w\.\-]+@[\w\.\-]+\.\w+)/i);
  if (emailMatch && emailMatch[1]) {
    email = emailMatch[1].trim();
  }

  // --- ATTEMPT 2: If no email found, parse for "From:" line in forwarded emails ---
  if (!email) {
    // This regex captures the name and the email from a line like "From: John Doe <user@example.com>"
    const fromMatch = description.match(/From:\s*(.*?)\s*<([\w\.\-]+@[\w\.\-]+\.\w+)>/i);
    if (fromMatch && fromMatch.length === 3) {
      // If a match is found, fromMatch[1] is the name and fromMatch[2] is the email
      name = fromMatch[1].trim();
      email = fromMatch[2].trim();
    }
  }

  // --- Parse Website URL (with a fallback) ---
  // First, look for a labeled URL
  const websiteMatchLabeled = description.match(/website url:\s*(https?:\/\/[^\s]+)/i);
  if (websiteMatchLabeled && websiteMatchLabeled[1]) {
    websiteUrl = websiteMatchLabeled[1].trim();
  } else {
    // If a labeled URL isn't found, look for the first URL in Jira's link format
    const websiteMatchGeneric = description.match(/\[(https?:\/\/[^\s|\]]+)/i);
    if (websiteMatchGeneric && websiteMatchGeneric[1]) {
      websiteUrl = websiteMatchGeneric[1].trim();
    }
  }

  // Construct the new item with the extracted data
  const newItem = {
    json: {
      name: name,
      email: email,
      websiteUrl: websiteUrl
    }
  };

  // Add the newly created item to our output array
  outputItems.push(newItem);
}

// Return all the processed items
return outputItems;
