  "üò°"
];

// ADD YOUR BOT TOKENS HERE
const botTokens = [
  "8241237833:AAEbJbNx4-9rTAHJZ_yaShzbqCRwUoD-I3U",  // replace with your own token
  "8339530330:AAG8EYMN1lLKK2yhmxl2fkFlQlKbc-lkuY",   // replace with your own token
  "8333742303:AA1kuLLW1x3WTN2i-9v8xXfjXZ2TSB_i1j"    // replace with your own token
];

// Get the original message text from user
const userMessage = telegramInput.message?.text || "";

// Extract message ID from Telegram link
const telegramLinkMatch = userMessage.match(/t\.me\/[^\/]+\/(\d+)/i);
const messageNumberMatch = userMessage.match(/message\s+(\d+)|post\s+(\d+)/i);

if (telegramLinkMatch) {
  messageId = parseInt(telegramLinkMatch[1]);
} else if (messageNumberMatch) {
  messageId = parseInt(messageNumberMatch[1] || messageNumberMatch[2]);
}

// Extract emojis from AI response
function extractArray(str) {
  if (typeof str !== 'string') return null;
  const match = str.match(/\[[\s\S]*?\]/);
  if (match) {
    try {
      return JSON.parse(match[0]);
    } catch (e) {
      return null;
    }
  }
  return null;
}

if (aiResponse.message && aiResponse.message.content) {
  const content = aiResponse.message.content;
  emojis = extractArray(content);
}
else if (aiResponse.message && typeof aiResponse.message === 'string') {
  emojis = extractArray(aiResponse.message);
}
else if (aiResponse.output) {
  if (typeof aiResponse.output === 'string') {
    emojis = extractArray(aiResponse.output);
  } else if (Array.isArray(aiResponse.output)) {
    emojis = aiResponse.output;
  }
}
else if (Array.isArray(aiResponse)) {
  emojis = aiResponse;
}
else if (aiResponse.choices && aiResponse.choices[0]?.message?.content) {
  const content = aiResponse.choices[0].message.content;
  emojis = extractArray(content);
}

if (!Array.isArray(emojis) || emojis.length === 0) {
  emojis = ["üëç", "‚ù§Ô∏è", "üî•"];
}

// If no message ID found, throw helpful error
if (!messageId) {
  throw new Error("‚ùå Please provide a Telegram message link!\n\nExample:\nhttps://t.me/n8nTesterChannel/62 needs 5 reactions please");
}

// Validate emojis
function getRandomValidEmoji(exclude = []) {
  const available = validEmojis.filter(e => !exclude.includes(e));
  return available[Math.floor(Math.random() * available.length)];
}

const uniqueEmojis = [...new Set(emojis)];
const allowDuplicates = uniqueEmojis.length <= 3;

const requestedCount = emojis.length;
const validatedEmojis = [];
const usedEmojis = new Set();

for (let emoji of emojis) {
  const cleaned = emoji.trim();

  if (validEmojis.includes(cleaned)) {
    if (allowDuplicates) {
      validatedEmojis.push(cleaned);
    } 
    else if (!usedEmojis.has(cleaned)) {
      validatedEmojis.push(cleaned);
      usedEmojis.add(cleaned);
    } else {
      let replacement = getRandomValidEmoji([...usedEmojis]);
      validatedEmojis.push(replacement);
      usedEmojis.add(replacement);
    }
  } else {
    let replacement = getRandomValidEmoji([...usedEmojis]);
    validatedEmojis.push(replacement);
    if (!allowDuplicates) {
      usedEmojis.add(replacement);
    }
  }
}

while (validatedEmojis.length < requestedCount) {
  let newEmoji = getRandomValidEmoji([...usedEmojis]);
  validatedEmojis.push(newEmoji);
  usedEmojis.add(newEmoji);
}

// Output with message ID and chat ID
const output = validatedEmojis.map((emoji, index) => ({
  emoji: emoji,
  botToken: botTokens[index % botTokens.length],
  chatId: chatId,
  messageId: messageId
}));

return output;

// Debug - check what we're sending
const items = $input.all();

console.log("Total items:", items.length);
items.forEach((item, index) => {
  console.log(`Item ${index}:`, {
