// Extract URL from WhatsApp message
const item = $input.item.json;

// Check if this is a message (not a status update)
if (!item.messages || !Array.isArray(item.messages) || item.messages.length === 0) {
  // This is a status update or empty, skip processing
  return null;
}

const message = item.messages[0];
const messageText = message.text?.body || '';
const from = message.from;

// Simple URL extraction
const urlRegex = /(https?:\/\/[^\s]+)/gi;
const urls = messageText.match(urlRegex);

if (!urls || urls.length === 0) {
  // No URL found, return error message
  return {
    error: true,
    from: from,
    message: "‚ö†Ô∏è Please send a valid URL to summarize. Example: https://example.com"
  };
}

return {
  URL: urls[0],
  from: from,
  original_message: messageText,
  error: false
};
