const crossrefData = $input.first().json.message?.items || [];
const semanticData = $input.all()[1]?.json?.data || [];
const openalexData = $input.all()[2]?.json?.results || [];

const references = [];

// Process CrossRef papers
crossrefData.forEach((paper, idx) => {
  references.push({
    id: `ref${references.length + 1}`,
    title: paper.title?.[0] || 'Unknown Title',
    authors: paper.author?.map(a => `${a.given} ${a.family}`).join(', ') || 'Unknown Author',
    year: paper.published?.['date-parts']?.[0]?.[0] || 'n.d.',
    venue: paper['container-title']?.[0] || 'Unknown Venue',
    doi: paper.DOI || '',
    url: paper.URL || '',
    abstract: paper.abstract || '',
    source: 'CrossRef'
  });
});

// Process Semantic Scholar papers
semanticData.forEach((paper, idx) => {
  references.push({
    id: `ref${references.length + 1}`,
    title: paper.title || 'Unknown Title',
    authors: paper.authors?.map(a => a.name).join(', ') || 'Unknown Author',
    year: paper.year || 'n.d.',
    venue: paper.venue || 'Unknown Venue',
    doi: '',
    url: paper.url || '',
    abstract: paper.abstract || '',
    citationCount: paper.citationCount || 0,
    source: 'Semantic Scholar'
  });
});

// Process OpenAlex papers
openalexData.forEach((paper, idx) => {
  references.push({
    id: `ref${references.length + 1}`,
    title: paper.title || 'Unknown Title',
    authors: paper.authorships?.map(a => a.author?.display_name).filter(Boolean).join(', ') || 'Unknown Author',
    year: paper.publication_year || 'n.d.',
    venue: paper.primary_location?.source?.display_name || 'Unknown Venue',
    doi: paper.doi || '',
    url: paper.doi ? `https://doi.org/${paper.doi.replace('https://doi.org/', '')}` : '',
    abstract: paper.abstract || '',
    source: 'OpenAlex'
  });
});

// Remove duplicates based on title similarity
const uniqueRefs = [];
const seenTitles = new Set();

references.forEach(ref => {
  const normalizedTitle = ref.title.toLowerCase().trim();
  if (!seenTitles.has(normalizedTitle)) {
    seenTitles.add(normalizedTitle);
    uniqueRefs.push(ref);
  }
});

return [{ json: { references: uniqueRefs.slice(0, 35) } }];

const title = $('Extract Input Data').item.json.paperTitle;
const abstract = $('Extract Input Data').item.json.paperAbstract;
const references = $('Process References').item.json.references;

const introduction = $input.all()[0]?.json?.output || '';
const literatureReview = $input.all()[1]?.json?.output || '';
const methodology = $input.all()[2]?.json?.output || '';
const results = $input.all()[3]?.json?.output || '';
const discussion = $input.all()[4]?.json?.output || '';
const conclusion = $input.all()[5]?.json?.output || '';

// Format references in APA style
const formattedReferences = references.map(ref => {
  const authors = ref.authors || 'Unknown Author';
  const year = ref.year || 'n.d.';
  const title = ref.title || 'Unknown Title';
  const venue = ref.venue || '';
  const doi = ref.doi ? `https://doi.org/${ref.doi}` : ref.url;

  return `${authors} (${year}). ${title}. ${venue}${venue ? '.' : ''} ${doi ? doi : ''}`;
}).join('\n\n');

// Compile full document
const fullDocument = `
TITLE: ${title}

ABSTRACT
${abstract}

1. INTRODUCTION
${introduction}

2. LITERATURE REVIEW
${literatureReview}

3. METHODOLOGY
${methodology}

4. RESULTS
${results}

5. DISCUSSION
${discussion}

6. CONCLUSION
${conclusion}

REFERENCES
${formattedReferences}
`;

return [{ 
  json: { 
    fullDocument,
    title,
    abstract,
    introduction,
    literatureReview,
    methodology,
    results,
    discussion,
    conclusion,
    references: formattedReferences,
    wordCount: fullDocument.split(/\s+/).length
  } 
}];
