const originalText = $('Get Text via Authenticated API Call').first().json.data;
const translatedText = $('Translate JP to EN').first().json.text;
const backTranslatedText = $('Back-Translate EN to JP').first().json.text;
const fileName = $('Watch for New Japanese Documents').first().json.name;

// Simple character-by-character diff
function generateDiff(original, backTranslated) {
  const maxLen = Math.max(original.length, backTranslated.length);
  let diffReport = [];

  for (let i = 0; i < maxLen; i++) {
    const origChar = original[i] || '';
    const backChar = backTranslated[i] || '';

    if (origChar !== backChar) {
      diffReport.push({
        position: i,
        original: origChar,
        backTranslated: backChar
      });
    }
  }

  return diffReport;
}

const differences = generateDiff(originalText, backTranslatedText);
const diffPercentage = ((differences.length / Math.max(originalText.length, backTranslatedText.length)) * 100).toFixed(2);

return [{
  json: {
    fileName,
    originalText,
    translatedText,
    backTranslatedText,
    differences,
    diffCount: differences.length,
    diffPercentage,
    summary: `Translation quality: ${100 - diffPercentage}% match between original and back-translation`
  }
}];
