const gradingResult = $input.first().json;
const studentName = $('Prepare Assignment Data').first().json.studentName;
const assignmentTitle = $('Prepare Assignment Data').first().json.assignmentTitle;

// Create HTML table
let htmlTable = `
<h2>Grading Report: ${assignmentTitle}</h2>
<h3>Student: ${studentName}</h3>
<table border="1" cellpadding="10" cellspacing="0" style="border-collapse: collapse; width: 100%;">
  <thead>
    <tr style="background-color: #4CAF50; color: white;">
      <th>Question</th>
      <th>Max Marks</th>
      <th>Marks Obtained</th>
      <th>Feedback</th>
    </tr>
  </thead>
  <tbody>
`;

gradingResult.questions.forEach(q => {
  htmlTable += `
    <tr>
      <td>Question ${q.questionNumber}</td>
      <td>${q.maxMarks}</td>
      <td>${q.marksObtained}</td>
      <td>${q.feedback}</td>
    </tr>
  `;
});

htmlTable += `
  </tbody>
  <tfoot>
    <tr style="background-color: #f2f2f2; font-weight: bold;">
      <td>TOTAL</td>
      <td>${gradingResult.totalMarks}</td>
      <td>${gradingResult.totalObtained}</td>
      <td>Grade: ${gradingResult.grade} (${gradingResult.percentage.toFixed(2)}%)</td>
    </tr>
  </tfoot>
</table>
<p><strong>Overall Feedback:</strong> ${gradingResult.overallFeedback}</p>
`;

// Create CSV data
let csvData = "Question,Max Marks,Marks Obtained,Feedback\n";
gradingResult.questions.forEach(q => {
  csvData += `"Question ${q.questionNumber}",${q.maxMarks},${q.marksObtained},"${q.feedback.replace(/"/g, '""')}"\n`;
});
csvData += `"TOTAL",${gradingResult.totalMarks},${gradingResult.totalObtained},"Grade: ${gradingResult.grade} (${gradingResult.percentage.toFixed(2)}%)"\n`;

return {
  studentName,
  assignmentTitle,
  htmlTable,
  csvData,
  gradingResult,
  summary: `${studentName} scored ${gradingResult.totalObtained}/${gradingResult.totalMarks} (${gradingResult.percentage.toFixed(2)}%) - Grade: ${gradingResult.grade}`
};
