const today = new Date();
const date30 = new Date(today);
date30.setDate(today.getDate() - 30);
const date60 = new Date(today);
date60.setDate(today.getDate() - 60);
const date90 = new Date(today);
date90.setDate(today.getDate() - 90);

return [{ 
  json: { 
    dateMinus30: date30.toISOString().split('T')[0],
    dateMinus60: date60.toISOString().split('T')[0],
    dateMinus90: date90.toISOString().split('T')[0],
    today: today.toISOString().split('T')[0],
    batchId: `BATCH_${Date.now()}`
  } 
}];

// Extract leads from response and add metadata
const leads = $input.all()[0].json.data || [];

const processedLeads = leads.map(lead => {
  const lastActivity = new Date(lead.Last_Activity_Time);
  const daysSinceContact = Math.floor((new Date() - lastActivity) / (1000 * 60 * 60 * 24));

  // Calculate priority tier
  let priorityTier;
  if (daysSinceContact >= 90) priorityTier = 'HOT';
  else if (daysSinceContact >= 60) priorityTier = 'WARM';
  else priorityTier = 'COLD';

  // Calculate engagement score
  const baseScore = lead.Lead_Score || 50;
  const timeDecay = Math.min(daysSinceContact / 30, 3) * 10;
  const engagementScore = Math.max(baseScore - timeDecay, 0);

  return {
    ...lead,
    daysSinceContact,
    priorityTier,
    engagementScore: Math.round(engagementScore),
    batchId: $('Calculate Date Ranges').item.json.batchId
  };
});

return processedLeads.map(lead => ({ json: lead }));

// Aggregate campaign results
const items = $input.all();
const totalProcessed = items.length;
const bySegment = {};
const byPriority = {};
const byVariant = { A: 0, B: 0 };

items.forEach(item => {
  const data = item.json;

  // Count by segment
  bySegment[data.segment] = (bySegment[data.segment] || 0) + 1;

  // Count by priority
  byPriority[data.priorityTier] = (byPriority[data.priorityTier] || 0) + 1;

  // Count by A/B variant
  byVariant[data.subjectLineVariant]++;
});

const avgEngagementScore = items.reduce((sum, item) => sum + (item.json.engagementScore || 0), 0) / totalProcessed;

return [{
  json: {
    batchId: items[0].json.batchId,
    completedAt: new Date().toISOString(),
    totalLeadsProcessed: totalProcessed,
    segmentBreakdown: bySegment,
    priorityBreakdown: byPriority,
    abTestSplit: byVariant,
    averageEngagementScore: Math.round(avgEngagementScore),
    campaignType: 'Cold Lead Revival'
  }
}];

