const deadline = new Date($json['deadline']);
const now = new Date();
const daysToDeadline = Math.ceil((deadline - now) / (1000 * 60 * 60 * 24));

return {
  json: {
    ...$json,
    daysToDeadline,
    isApproaching: daysToDeadline > 0 && daysToDeadline <= 3,
    isPassed: daysToDeadline <= 0
  }
};
