// SDK Release Drift Calculator
// Compares release dates with FAQ update dates to identify documentation lag

const items = [];
const allItems = $input.all();

for (let i = 0; i < allItems.length; i++) {
  const item = allItems[i].json;

  // Process SDK release data from Google Sheets
  if (item["SDK Name"]) {
    const sdkName = item["SDK Name"];
    const releaseDate = new Date(item["Release Date"]);
    const releaseTag = item["Release Tag"];
    const releaseUrl = item["Release Url"];
    const daysSinceRelease = parseInt(item["Days Since Release"]);

    // Calculate FAQ update drift
    let faqUpdateDate;
    let daysSinceFaqUpdate;

    if (item["Last FAQ Update(Notion)"] && item["Last FAQ Update(Notion)"] !== "") {
      // FAQ has been updated - use that date
      faqUpdateDate = new Date(item["Last FAQ Update(Notion)"]);
      daysSinceFaqUpdate = Math.floor((new Date() - faqUpdateDate) / (1000 * 60 * 60 * 24));
    } else {
      // No FAQ update recorded - use release date as baseline
      faqUpdateDate = releaseDate;
      daysSinceFaqUpdate = daysSinceRelease;
    }

    // Determine if documentation is overdue (>30 days)
    const OVERDUE_THRESHOLD = 30;
    const isOverdue = daysSinceFaqUpdate > OVERDUE_THRESHOLD;

    items.push({
      json: {
        sdkName: sdkName,
        latestRelease: releaseTag,
        releaseDate: item["Release Date"],
        releaseUrl: releaseUrl,
        daysSinceRelease: daysSinceRelease,
        lastFaqUpdate: faqUpdateDate.toISOString(),
        daysSinceFaqUpdate: daysSinceFaqUpdate,
        isOverdue: isOverdue,
        overdueStatus: isOverdue ? "OVERDUE" : "OK"
      }
    });
  }
}

return items;

