parameters.jsCode:
// Travel Itinerary Combination Engine
// Combines flights and hotels into ranked packages

const inputData = $input.all();

// Extract flight and hotel data from merged inputs
let flightData = [];
let hotelData = [];
let searchParams = {};

// Parse the merged data
for (const item of inputData) {
  if (item.json.data && item.json.data.itineraries) {
    // Flight data
    const flights = item.json.data.itineraries.results || [];
    flightData = flights.slice(0, 10); // Top 10 flights
  } else if (item.json.result) {
    // Hotel data
    const hotels = item.json.result || [];
    hotelData = hotels.slice(0, 10); // Top 10 hotels
  }

  // Capture search parameters
  if (item.json.destination) {
    searchParams = {
      destination: item.json.destination,
      departure: item.json.departure,
      checkInDate: item.json.checkInDate,
      checkOutDate: item.json.checkOutDate,
      notificationEmail: item.json.notificationEmail,
      adults: item.json.adults || 1
    };
  }
}

// Fallback: Create mock data if APIs failed
if (flightData.length === 0) {
  console.log('No flight data - using mock data');
  flightData = [
    {
      id: 'mock-flight-1',
      price: { raw: 650, formatted: '$650' },
      legs: [{
        origin: { displayCode: searchParams.departure || 'NYC' },
        destination: { displayCode: searchParams.destination || 'PVG' },
        departure: searchParams.checkInDate + 'T08:00:00',
        arrival: searchParams.checkInDate + 'T14:30:00',
        durationInMinutes: 870,
        carriers: { marketing: [{ name: 'United Airlines' }] }
      }]
    },
    {
      id: 'mock-flight-2',
      price: { raw: 720, formatted: '$720' },
      legs: [{
        origin: { displayCode: searchParams.departure || 'NYC' },
        destination: { displayCode: searchParams.destination || 'PVG' },
        departure: searchParams.checkInDate + 'T10:30:00',
        arrival: searchParams.checkInDate + 'T17:00:00',
        durationInMinutes: 870,
        carriers: { marketing: [{ name: 'Delta Airlines' }] }
      }]
    },
    {
      id: 'mock-flight-3',
      price: { raw: 580, formatted: '$580' },
      legs: [{
        origin: { displayCode: searchParams.departure || 'NYC' },
        destination: { displayCode: searchParams.destination || 'PVG' },
        departure: searchParams.checkInDate + 'T14:00:00',
        arrival: searchParams.checkInDate + 'T20:30:00',
        durationInMinutes: 870,
        carriers: { marketing: [{ name: 'American Airlines' }] }
      }]
    }
  ];
}

if (hotelData.length === 0) {
  console.log('No hotel data - using mock data');
  const nights = calculateNights(searchParams.checkInDate, searchParams.checkOutDate);
  hotelData = [
    {
      hotel_id: 'mock-hotel-1',
      hotel_name: 'Shanghai Grand Hotel',
      price_breakdown: { gross_price: 150 * nights },
      review_score: 8.5,
      address: 'Pudong District, Shanghai',
      url: 'https://booking.com/hotel-1',
      pricePerNight: 150,
      totalNights: nights
    },
    {
      hotel_id: 'mock-hotel-2',
      hotel_name: 'Bund Riverside Inn',
      price_breakdown: { gross_price: 120 * nights },
      review_score: 8.2,
      address: 'The Bund, Shanghai',
      url: 'https://booking.com/hotel-2',
      pricePerNight: 120,
      totalNights: nights
    },
    {
      hotel_id: 'mock-hotel-3',
      hotel_name: 'Lujiazui Business Hotel',
      price_breakdown: { gross_price: 180 * nights },
      review_score: 8.8,
      address: 'Lujiazui, Shanghai',
      url: 'https://booking.com/hotel-3',
      pricePerNight: 180,
      totalNights: nights
    }
  ];
}

// Calculate nights
function calculateNights(checkIn, checkOut) {
  const start = new Date(checkIn);
  const end = new Date(checkOut);
  return Math.ceil((end - start) / (1000 * 60 * 60 * 24));
}

const nights = calculateNights(searchParams.checkInDate, searchParams.checkOutDate);

// Process and normalize flight data
const processedFlights = flightData.map(flight => {
  const leg = flight.legs ? flight.legs[0] : {};
  return {
    id: flight.id,
    airline: leg.carriers?.marketing?.[0]?.name || 'Unknown Airline',
    origin: leg.origin?.displayCode || searchParams.departure,
    destination: leg.destination?.displayCode || searchParams.destination,
    departure: leg.departure || searchParams.checkInDate,
    arrival: leg.arrival || searchParams.checkInDate,
    duration: leg.durationInMinutes ? `${Math.floor(leg.durationInMinutes / 60)}h ${leg.durationInMinutes % 60}m` : 'N/A',
    price: flight.price?.raw || 0,
    priceFormatted: flight.price?.formatted || `$${flight.price?.raw || 0}`,
    bookingLink: `https://www.skyscanner.com/transport/flights/${leg.origin?.displayCode}/${leg.destination?.displayCode}`
  };
});

// Process and normalize hotel data
const processedHotels = hotelData.map(hotel => {
  const totalPrice = hotel.price_breakdown?.gross_price || hotel.pricePerNight * nights || 0;
  return {
    id: hotel.hotel_id,
    name: hotel.hotel_name || 'Unknown Hotel',
    rating: hotel.review_score || 0,
    address: hotel.address || searchParams.destination,
    pricePerNight: hotel.pricePerNight || Math.round(totalPrice / nights),
    totalPrice: totalPrice,
    nights: nights,
    bookingLink: hotel.url || `https://www.booking.com/hotel/${hotel.hotel_id}.html`
  };
});

// Create all possible combinations
const itineraries = [];
for (const flight of processedFlights) {
  for (const hotel of processedHotels) {
    itineraries.push({
      id: `${flight.id}-${hotel.id}`,
      flight: flight,
      hotel: hotel,
      totalPrice: flight.price + hotel.totalPrice,
      savings: 0 // Will calculate after sorting
    });
  }
}

// Sort by total price (cheapest first)
itineraries.sort((a, b) => a.totalPrice - b.totalPrice);

// Calculate savings compared to most expensive option
const mostExpensive = itineraries[itineraries.length - 1].totalPrice;
itineraries.forEach(item => {
  item.savings = mostExpensive - item.totalPrice;
});

// Get top 5 itineraries
const topItineraries = itineraries.slice(0, 5);

// Return result with metadata
return [{
  json: {
    searchParams: searchParams,
    itineraries: topItineraries,
    totalCombinations: itineraries.length,
    flightsFound: processedFlights.length,
    hotelsFound: processedHotels.length,
    generatedAt: new Date().toISOString()
  }
}];

