// Parse OpenAI-style responses into { prompt, caption, hashtags[], hashtags_string }
return $input.all().map(item => {
  const data = item.json || {};

  // Try to locate "content" in the common places
  let content =
    data?.choices?.[0]?.message?.content ??
    data?.message?.content ??
    data?.content ??
    null;

  let prompt = "";
  let caption = "";
  let hashtagsArr = [];

  // If content is an object (your current input case)
  if (content && typeof content === "object") {
    prompt = content.prompt ?? "";
    caption = content.caption ?? "";
    if (Array.isArray(content.hashtags)) {
      hashtagsArr = content.hashtags;
    } else if (typeof content.hashtags === "string") {
      // Support comma/space separated string
      hashtagsArr = content.hashtags.split(/[,\s]+/).filter(Boolean);
    }
  }
  // If content is a string (some models return JSON as string)
  else if (typeof content === "string" && content.trim()) {
    try {
      const parsed = JSON.parse(content);
      prompt = parsed.prompt ?? "";
      caption = parsed.caption ?? "";
      if (Array.isArray(parsed.hashtags)) {
        hashtagsArr = parsed.hashtags;
      } else if (typeof parsed.hashtags === "string") {
        hashtagsArr = parsed.hashtags.split(/[,\s]+/).filter(Boolean);
      }
    } catch {
      // Not a JSON string ‚Äî leave fields empty
      prompt = "";
      caption = "";
      hashtagsArr = [];
    }
  }

  // Normalize hashtags: ensure leading '#', dedupe, no empties
  const norm = Array.from(
    new Set(
      (hashtagsArr || [])
        .map(h => (h ?? "").toString().trim())
        .filter(Boolean)
        .map(h => (h.startsWith("#") ? h : `#${h}`))
    )
  );
  const hashtags_string = norm.join(" ");

  return {
    json: {
      prompt,
      caption,
      hashtags: norm,
      hashtags_string
    }
  };
});

// Parse Google Sheets Input (headers) and Validate for Veo 3.1 workflow
const items = $input.all();
const results = [];

// Helper to read and trim a field safely
function getField(data, key) {
  const v = (data[key] ?? data[key?.toUpperCase?.()] ?? "").toString();
  return v.trim();
}

for (const item of items) {
  const data = item.json || {};

  // Read by header names
  const id_video  = getField(data, "id_video");
  const niche     = getField(data, "niche");
  const idea      = getField(data, "idea");
  const url_1     = getField(data, "url_1");
  const url_2     = getField(data, "url_2");
  const url_3     = getField(data, "url_3");
  const url_final = getField(data, "url_final");   // peut √™tre vide au d√©part
  const status    = getField(data, "status");      // ex: pending|processing|completed|failed
  const row_number = data.row ?? data._row ?? "";

  // Validation
  const errors = [];

  // niche & idea
  if (!niche || niche.length <= 2) {
    errors.push("niche must be longer than 2 characters");
  }
  if (!idea || idea.length <= 5) {
    errors.push("idea must be longer than 5 characters");
  }

  // URLs images requises
  if (!url_1 || !url_1.startsWith("http")) {
    errors.push("url_1 is missing or invalid - must start with http");
  }
  if (!url_2 || !url_2.startsWith("http")) {
    errors.push("url_2 is missing or invalid - must start with http");
  }
  if (!url_3 || !url_3.startsWith("http")) {
    errors.push("url_3 is missing or invalid - must start with http");
  }

  // url_final si pr√©sent
  if (url_final && !url_final.startsWith("http")) {
    errors.push("url_final is invalid - must start with http if provided");
  }

  // status optionnel : normalisation (facultatif)
  const allowedStatus = new Set(["", "pending", "processing", "completed", "failed", "draft"]);
  if (!allowedStatus.has(status.toLowerCase?.() ?? "")) {
    errors.push(`status '${status}' is not recognized (use: pending|processing|completed|failed|draft)`);
  }

  // Build output item
  const image_urls = [url_1, url_2, url_3];

  const out = {
    json: {
      id_video,
      niche,
      idea,
      image_urls,
      url_final: url_final || "",
      status: (status || "pending").toLowerCase(),
      row_number,
      timestamp: new Date().toISOString(),
    },
  };

  // Valid flag + errors
  if (errors.length) {
    out.json.valid = false;
    out.json.validation_errors = errors;
  } else {
    out.json.valid = true;
  }

  results.push(out);
}

return results;

const prompt = $input.item.json.veo_prompt || $input.item.json.prompt;
let imageUrls = $('Parse Sheet Input').item.json.image_urls;

function convertGoogleDriveUrl(url) {
  if (!url) return url;
  if (url.includes('drive.google.com/file/d/')) {
    const match = url.match(/\/d\/([a-zA-Z0-9_-]+)/);
    if (match && match[1]) {
      return `https://drive.google.com/uc?export=download&id=${match[1]}`;
    }
  }
  return url;
}

if (!prompt || prompt.length < 10) {
  throw new Error('Prompt required');
}

if (!Array.isArray(imageUrls) || imageUrls.length !== 3) {
  throw new Error('Need 3 image URLs');
}

const directImageUrls = imageUrls.map(url => convertGoogleDriveUrl(url));

directImageUrls.forEach((url, index) => {
  if (!url || !url.startsWith('http')) {
    throw new Error('Invalid image URL at position ' + (index + 1));
  }
});

return {
  json: {
    veo_request_body: {
      prompt: prompt,
      image_urls: directImageUrls,
      duration: 8,
      aspect_ratio: "9:16"
    },
    ...($input.item.json)
  }
};

// Extract video data from Veo Generation response
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json || {};

  // Extract video data from current node
  const video_url = data.video?.url || '';
  const video_content_type = data.video?.content_type || '';
  const video_file_size = data.video?.file_size || 0;
  const video_file_name = data.video?.file_name || '';

  // Preserve data from previous nodes
  const niche = $('Parse Sheet Input').item.json.niche || '';
  const idea = $('Parse Sheet Input').item.json.idea || '';
  const caption = $('Parse GPT Response').item.json.caption || '';
  const hashtags_string = $('Parse GPT Response').item.json.hashtags_string || '';
  const veo_prompt = $('Optimize Prompt for Veo').item.json.veo_prompt || '';
  const id_video = $('Parse Sheet Input').item.json.id_video || '';
  const row_number = $('Parse Sheet Input').item.json.row_number || '';

  results.push({
    json: {
      video_url,
      video_content_type,
      video_file_size,
      video_file_name,
      generation_completed_at: new Date().toISOString(),
      niche,
      idea,
      caption,
      hashtags_string,
      veo_prompt,
      id_video,
      row_number
    }
  });
}

return results;
