// Extract message data from webhook payload
const messageText = $input.item.json.body.message || $input.item.json.body.text;
const senderId = $input.item.json.body.sender_id || $input.item.json.body.user_id;
const groupId = $input.item.json.body.group_id || $input.item.json.body.chat_id;
const senderName = $input.item.json.body.sender_name || 'User';

return {
  json: {
    originalMessage: messageText,
    senderId: senderId,
    groupId: groupId,
    senderName: senderName,
    timestamp: new Date().toISOString()
  }
};

// Extract detected language code
const detectedLanguage = $input.item.json.choices[0].message.content.trim().toLowerCase();

return {
  json: {
    ...($input.item.json),
    detectedLanguage: detectedLanguage,
    originalMessage: $('Extract Message Data').item.json.originalMessage,
    senderId: $('Extract Message Data').item.json.senderId,
    groupId: $('Extract Message Data').item.json.groupId,
    senderName: $('Extract Message Data').item.json.senderName
  }
};

// Define group members and their preferred languages
// In production, this would come from a database
const groupMembers = {
  'group_123': [
    { userId: 'user_1', name: 'John', language: 'en' },
    { userId: 'user_2', name: 'Mar√≠a', language: 'es' },
    { userId: 'user_3', name: 'Pierre', language: 'fr' },
    { userId: 'user_4', name: 'Hans', language: 'de' },
    { userId: 'user_5', name: 'Rajesh', language: 'hi' },
    { userId: 'user_6', name: 'Ahmed', language: 'ar' },
    { userId: 'user_7', name: 'Yuki', language: 'ja' },
    { userId: 'user_8', name: 'Wei', language: 'zh' }
  ]
};

const groupId = $input.item.json.groupId;
const senderId = $input.item.json.senderId;
const detectedLanguage = $input.item.json.detectedLanguage;

// Get all members of the group except the sender
const members = groupMembers[groupId] || [];
const recipients = members.filter(member => member.userId !== senderId);

// Get unique target languages
const targetLanguages = [...new Set(recipients.map(r => r.language))];

// Remove the source language from targets if present
const languagesToTranslate = targetLanguages.filter(lang => lang !== detectedLanguage);

return languagesToTranslate.map(language => ({
  json: {
    targetLanguage: language,
    originalMessage: $input.item.json.originalMessage,
    detectedLanguage: detectedLanguage,
    senderId: senderId,
    senderName: $input.item.json.senderName,
    groupId: groupId,
    recipients: recipients.filter(r => r.language === language)
  }
}));

// Extract translated text and combine with recipient info
const translatedText = $input.item.json.choices[0].message.content.trim();
const originalData = $('Get Group Members & Languages').item.json;

return {
  json: {
    translatedMessage: translatedText,
    targetLanguage: originalData.targetLanguage,
    originalMessage: originalData.originalMessage,
    senderName: originalData.senderName,
    recipients: originalData.recipients,
    groupId: originalData.groupId
  }
};

// Create individual messages for each recipient
const recipients = $input.item.json.recipients;
const translatedMessage = $input.item.json.translatedMessage;
const senderName = $input.item.json.senderName;
const targetLanguage = $input.item.json.targetLanguage;

return recipients.map(recipient => ({
  json: {
    recipientId: recipient.userId,
    recipientName: recipient.name,
    recipientLanguage: recipient.language,
    message: `${senderName}: ${translatedMessage}`,
    translatedMessage: translatedMessage,
    senderName: senderName,
    targetLanguage: targetLanguage
  }
}));

// Log successful delivery
const deliveryLog = {
  timestamp: new Date().toISOString(),
  recipientId: $input.item.json.recipientId,
  recipientName: $('Split to Individual Recipients').item.json.recipientName,
  language: $('Split to Individual Recipients').item.json.targetLanguage,
  status: 'delivered',
  messageLength: $('Split to Individual Recipients').item.json.translatedMessage.length
};

return {
  json: deliveryLog
};
