parameters.value:
={{
(() => {
  // Title from your writer node
  const t =
    $node["Create post title and structure"].json?.message?.content?.title || "";

  // Keep only allowed, crash-related tokens
  const allow = new Set([
    "car","vehicle","truck","semi","collision","crash","wreck","pileup",
    "freeway","highway","traffic","police","chp","ontario","california"
  ]);

  const tokens = t
    .toLowerCase()
    .replace(/[^a-z0-9 ]+/g, " ")
    .split(/\s+/)
    .filter(w => allow.has(w));

  // Fallback if nothing matched
  const q = (tokens.length ? tokens.join("+") : "car+crash+freeway+accident");
  return q;
})()
}}

parameters.url:
={{
(() => {
  const hits = $node["Pixabay HTTP"].json?.hits || [];
  if (!hits.length) {
    // Fallback image so the flow still succeeds when no results
    return "https://images.pexels.com/photos/427679/pexels-photo-427679.jpeg";
  }
  const h = hits[0];
  return h.largeImageURL || h.webformatURL || h.previewURL;
})()
}}
