// Prepare analysis context and generate a unique ID
const analysisData = {
  // Use bracket notation for keys with spaces
  target_url: $json["Target URL"],
  analysis_focus: $json["Analysis Focus"],
  industry: $json["Industry"],

  timestamp: new Date().toISOString(),
  analysis_id: 'CA_' + Date.now(),

  // Fix the replace function by referencing the correct key
  lookup_key: $json["Target URL"].replace(/https?:\/\//, '').replace(/\/$/, '')
};

return analysisData;

