// This function assumes the input item (from Job Data Parser)
// looks like this:
// {
//   "output": {
//     "company_name": "TechSolutions Inc.",
//     "job_listings": ["Job Title 1", "Job Title 2", ...],
//     "links": ["Link 1", "Link 2", ...]
//   }
// }

const inputItem = $input.first().json;
const jobData = inputItem.output; // Adjust 'output' if the structure is slightly different

// Extract the parallel arrays and company name
const jobTitles = jobData.job_listings || [];
const links = jobData.links || [];
const company = jobData.company_name || '';

const newJobItems = [];

// Loop through the job titles and create a new item for each one
for (let i = 0; i < jobTitles.length; i++) {
  // Ensure both job title and link exist for this index
  if (jobTitles[i] && links[i]) {
    newJobItems.push({
      json: {
        company: company,
        job_listing: jobTitles[i],
        link: links[i],
      }
    });
  }
}

// Return the array of new n8n items
return newJobItems;

// The input is a single list containing items from both
// 'Create Job Item List (7)' (new jobs) and
// 'Get All Existing Jobs (8)' (existing jobs, which have a 'row_number').

const allItems = $input.all().map(item => item.json);

// Separate the items based on the presence of the 'row_number' property
// Existing jobs from Google Sheets always have 'row_number'.
// New scraped jobs do not.
const newJobs = allItems.filter(item => !item.row_number);
const existingJobs = allItems.filter(item => item.row_number);

// Create a set of existing job titles for efficient lookup.
// We look for 'job_listing' in the existing jobs array.
const existingTitles = new Set(existingJobs.map(row => (row.job_listing || '').trim().toLowerCase()));

// Filter out only unique new jobs (not already in the sheet)
const uniqueNewJobs = newJobs.filter(job => {
  const jobTitle = (job.job_listing || '').trim().toLowerCase();

  // A job is unique and should be kept if:
  // 1. Its title is not empty.
  // 2. Its title is NOT present in the set of existing job titles.
  return jobTitle.length > 0 && !existingTitles.has(jobTitle);
});

// Return results in n8n format
return uniqueNewJobs.map(job => ({ json: job }));

