:root {
	--font-family: 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;

	/* Typography ‚Äî larger & more readable */
	--font-size-body: 16px;
	--font-size-label: 15px;
	--font-size-test-notice: 13px;
	--font-size-input: 15px;
	--font-size-header: 24px;
	--font-size-paragraph: 16px;
	--font-size-link: 14px;
	--font-size-error: 13px;
	--font-size-html-h1: 34px;
	--font-size-html-h2: 26px;
	--font-size-html-h3: 20px;
	--font-size-html-h4: 18px;
	--font-size-html-h5: 16px;
	--font-size-html-h6: 14px;
	--font-size-subheader: 16px;

	/* Colors ‚Äî higher contrast & friendlier accents */
	--color-background: #f7f8fc;
	--color-test-notice-text: #b45309;     /* deeper amber for contrast */
	--color-test-notice-bg: #fff7ed;
	--color-test-notice-border: #fed7aa;

	--color-card-bg: #ffffff;
	--color-card-border: #e5e7eb;
	--color-card-shadow: rgba(31, 41, 55, 0.06);

	--color-link: #2563eb;                 /* accessible blue for standard links */
	--color-header: #1f2937;               /* slate-800 */
	--color-label: #374151;                /* slate-700 */
	--color-input-border: #cbd5e1;         /* slate-300 */
	--color-input-text: #111827;           /* slate-900 */
	--color-focus-border: #7c3aed;         /* vivid violet focus ring */
	--color-submit-btn-bg: #ff6d5a;        /* brand coral kept */
	--color-submit-btn-text: #ffffff;
	--color-error: #dc2626;                /* red-600 */
	--color-required: #ef4444;             /* red-500 */
	--color-clear-button-bg: #6b7280;      /* slate-500 */
	--color-html-text: #374151;            /* slate-700 */
	--color-html-link: #ff6d5a;            /* brand accent for content links */
	--color-header-subtext: #6b7280;       /* slate-500 */

	/* Border Radii ‚Äî slightly softer */
	--border-radius-card: 10px;
	--border-radius-input: 8px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 10px;

	/* Spacing ‚Äî room to breathe */
	--padding-container-top: 32px;
	--padding-card: 28px;
	--padding-test-notice-vertical: 12px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 20px;
	--padding-form-input: 14px;
	--card-padding: 28px;
	--card-margin-bottom: 20px;

	/* Dimensions ‚Äî wider layout */
	--container-width: 720px;
	--submit-btn-height: 52px;
	--checkbox-size: 20px;

	/* Others ‚Äî subtle shadow & clearer placeholders */
	--box-shadow-card: 0px 6px 20px 0px var(--color-card-shadow);
	--opacity-placeholder: 0.6;
}
