// Get visitor data from the Set node
const visitorData = $('Set - Extract Form Data').item.json;

// Generate unique visitor ID
const timestamp = Date.now();
const dateStr = new Date().toISOString().split('T')[0].replace(/-/g, '');
const visitorId = `VIS-${dateStr}-${timestamp.toString().slice(-6)}`;

// Create QR code data with all visitor information
const qrCodeData = JSON.stringify({
  id: visitorId,
  name: visitorData.visitorName,
  email: visitorData.visitorEmail,
  date: visitorData.visitDate,
  purpose: visitorData.purpose,
  company: visitorData.company,
  timestamp: new Date().toISOString(),
  verified: true
});

// Simple format for basic QR readers (alternative)
const qrCodeSimple = `VISITOR|${visitorId}|${visitorData.visitorName}|${visitorData.visitDate}|${visitorData.company}`;

// Generate QR code image URL
const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=300x300&data=${encodeURIComponent(qrCodeSimple)}`;

// Alternative high-quality QR code API
const qrCodeUrlAlt = `https://quickchart.io/qr?text=${encodeURIComponent(qrCodeSimple)}&size=300&margin=2`;

// Return the generated data
return {
  json: {
    visitorId: visitorId,
    qrCodeData: qrCodeData,
    qrCodeSimple: qrCodeSimple,
    qrCodeUrl: qrCodeUrl,
    qrCodeUrlAlt: qrCodeUrlAlt,
    generatedAt: new Date().toISOString()
  }
};

