const currentData = $input.first().json;
const historicalData = $input.all().map(item => item.json);

// Calculate averages from last 7 days
const last7Days = historicalData.slice(0, 7);
const avgHeartRate = last7Days.reduce((sum, d) => sum + parseFloat(d.heart_rate || 0), 0) / last7Days.length;
const avgBPSystolic = last7Days.reduce((sum, d) => sum + parseFloat(d.bp_systolic || 0), 0) / last7Days.length;
const avgBPDiastolic = last7Days.reduce((sum, d) => sum + parseFloat(d.bp_diastolic || 0), 0) / last7Days.length;
const avgSleep = last7Days.reduce((sum, d) => sum + parseFloat(d.sleep_hours || 0), 0) / last7Days.length;

// Health analysis flags
const alerts = [];
let riskLevel = 'normal';

// Heart rate analysis
if (currentData.heart_rate > 100 || currentData.heart_rate < 60) {
  alerts.push(`Heart rate abnormal: ${currentData.heart_rate} bpm`);
  riskLevel = 'warning';
}

// Blood pressure analysis
if (currentData.bp_systolic > 140 || currentData.bp_diastolic > 90) {
  alerts.push(`High blood pressure: ${currentData.bp_systolic}/${currentData.bp_diastolic}`);
  riskLevel = 'critical';
}
if (currentData.bp_systolic < 90 || currentData.bp_diastolic < 60) {
  alerts.push(`Low blood pressure: ${currentData.bp_systolic}/${currentData.bp_diastolic}`);
  riskLevel = 'warning';
}

// Temperature analysis
if (currentData.temperature > 38.0) {
  alerts.push(`Elevated temperature: ${currentData.temperature}¬∞C`);
  riskLevel = 'critical';
}

// Sleep analysis
if (avgSleep < 6) {
  alerts.push(`Insufficient sleep average: ${avgSleep.toFixed(1)} hours`);
  if (riskLevel === 'normal') riskLevel = 'warning';
}

// Symptoms check
if (currentData.symptoms && currentData.symptoms !== 'none') {
  alerts.push(`Reported symptoms: ${currentData.symptoms}`);
  if (riskLevel === 'normal') riskLevel = 'warning';
}

const needsDoctorVisit = riskLevel === 'critical' || alerts.length >= 3;

return {
  currentData,
  analysis: {
    alerts,
    riskLevel,
    needsDoctorVisit,
    averages: {
      heartRate: avgHeartRate.toFixed(1),
      bloodPressure: `${avgBPSystolic.toFixed(0)}/${avgBPDiastolic.toFixed(0)}`,
      sleep: avgSleep.toFixed(1)
    },
    trend: {
      heartRateChange: ((currentData.heart_rate - avgHeartRate) / avgHeartRate * 100).toFixed(1),
      sleepChange: ((currentData.sleep_hours - avgSleep) / avgSleep * 100).toFixed(1)
    }
  }
};

