const data = $input.first().json;
const current = data.currentData;
const analysis = data.analysis;

// Weighted health scoring algorithm (0-100)
let healthScore = 100;

// Heart rate scoring (-20 to 0)
const hrDiff = Math.abs(current.heart_rate - 75);
if (hrDiff > 25) healthScore -= 20;
else if (hrDiff > 15) healthScore -= 10;
else if (hrDiff > 5) healthScore -= 5;

// Blood pressure scoring (-25 to 0)
const bpSystolic = parseFloat(current.bp_systolic);
const bpDiastolic = parseFloat(current.bp_diastolic);
if (bpSystolic > 140 || bpDiastolic > 90) healthScore -= 25;
else if (bpSystolic > 130 || bpDiastolic > 85) healthScore -= 15;
else if (bpSystolic < 90 || bpDiastolic < 60) healthScore -= 20;
else if (bpSystolic > 120 || bpDiastolic > 80) healthScore -= 8;

// Temperature scoring (-15 to 0)
const temp = parseFloat(current.temperature);
if (temp > 38.5) healthScore -= 15;
else if (temp > 37.5) healthScore -= 8;
else if (temp < 36.0) healthScore -= 10;

// Sleep scoring (-15 to 0)
const sleep = parseFloat(current.sleep_hours);
if (sleep < 5) healthScore -= 15;
else if (sleep < 6) healthScore -= 10;
else if (sleep < 7) healthScore -= 5;
else if (sleep > 10) healthScore -= 5;

// Activity scoring (-15 to 0)
const steps = parseInt(current.steps_count);
if (steps < 3000) healthScore -= 15;
else if (steps < 5000) healthScore -= 10;
else if (steps < 7000) healthScore -= 5;

// Symptoms scoring (-10 to 0)
if (current.symptoms && current.symptoms !== 'none') {
  const symptomSeverity = (current.symptoms.match(/severe|intense|extreme/i)) ? 10 : 5;
  healthScore -= symptomSeverity;
}

// Ensure score stays within 0-100
healthScore = Math.max(0, Math.min(100, healthScore));

// Determine health grade
let grade, recommendation;
if (healthScore >= 90) {
  grade = 'A+ Excellent';
  recommendation = 'Maintain current healthy lifestyle';
} else if (healthScore >= 80) {
  grade = 'A Good';
  recommendation = 'Minor improvements recommended';
} else if (healthScore >= 70) {
  grade = 'B Fair';
  recommendation = 'Focus on sleep and exercise';
} else if (healthScore >= 60) {
  grade = 'C Concerning';
  recommendation = 'Medical consultation advised within 1 week';
} else if (healthScore >= 50) {
  grade = 'D Poor';
  recommendation = 'Schedule doctor appointment within 48 hours';
} else {
  grade = 'F Critical';
  recommendation = 'URGENT: Seek immediate medical attention';
}

return {
  ...data,
  healthScore: {
    score: healthScore,
    grade: grade,
    recommendation: recommendation,
    breakdown: {
      cardiovascular: Math.max(0, 100 - (hrDiff * 2) - ((bpSystolic > 140) ? 25 : 0)),
      rest: Math.max(0, 100 - ((sleep < 7) ? (7 - sleep) * 10 : 0)),
      activity: Math.max(0, 100 - ((steps < 7000) ? ((7000 - steps) / 70) : 0)),
      vitals: Math.max(0, 100 - ((temp > 37.5) ? (temp - 37.5) * 20 : 0))
    },
    timestamp: new Date().toISOString()
  }
};

const currentData = $input.first().json;
const historicalData = $input.all().map(item => item.json);

// Get data from 7 days ago (assuming daily records)
const previousWeek = historicalData.slice(7, 14);
if (previousWeek.length === 0) {
  return { ...currentData, weeklyComparison: { available: false } };
}

// Calculate previous week averages
const prevAvgHR = previousWeek.reduce((sum, d) => sum + parseFloat(d.heart_rate || 0), 0) / previousWeek.length;
const prevAvgBP = previousWeek.reduce((sum, d) => sum + parseFloat(d.bp_systolic || 0), 0) / previousWeek.length;
const prevAvgSleep = previousWeek.reduce((sum, d) => sum + parseFloat(d.sleep_hours || 0), 0) / previousWeek.length;
const prevAvgSteps = previousWeek.reduce((sum, d) => sum + parseFloat(d.steps_count || 0), 0) / previousWeek.length;

// Current week averages (already in analysis)
const currAvgHR = parseFloat(currentData.analysis.averages.heartRate);
const currAvgSleep = parseFloat(currentData.analysis.averages.sleep);

// Calculate week-over-week changes
const weeklyComparison = {
  available: true,
  heartRateChange: ((currAvgHR - prevAvgHR) / prevAvgHR * 100).toFixed(1),
  sleepChange: ((currAvgSleep - prevAvgSleep) / prevAvgSleep * 100).toFixed(1),
  trend: (currAvgHR < prevAvgHR && currAvgSleep > prevAvgSleep) ? 'improving' : 'needs attention',
  insights: []
};

if (Math.abs(weeklyComparison.heartRateChange) > 10) {
  weeklyComparison.insights.push(`Significant heart rate change: ${weeklyComparison.heartRateChange}%`);
}
if (Math.abs(weeklyComparison.sleepChange) > 15) {
  weeklyComparison.insights.push(`Notable sleep pattern change: ${weeklyComparison.sleepChange}%`);
}

return { ...currentData, weeklyComparison };

